import React, { useState, useEffect } from 'react';
import { Container, Card, Row, Col, Form, Button, Spinner, Table } from 'react-bootstrap';

const AddStudentCourses = () => {
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [studentCourses, setStudentCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [adding, setAdding] = useState(false);

  useEffect(() => {
    // ÿ¨ŸÑÿ® ÿßŸÑÿ∑ŸÑÿßÿ® ŸàÿßŸÑÿØŸàÿ±ÿßÿ™ ŸÖŸÜ ÿßŸÑŸÄ API
    const fetchData = async () => {
      setLoading(true);
      try {
        const studentResponse = await fetch('/api/students');
        const courseResponse = await fetch('/api/courses');
        const studentsData = await studentResponse.json();
        const coursesData = await courseResponse.json();
        setStudents(studentsData);
        setCourses(coursesData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
      setLoading(false);
    };

    fetchData();
  }, []);

  const handleAddCourse = async () => {
    if (!selectedStudent || !selectedCourse) return;

    setAdding(true);
    try {
      const response = await fetch('http://localhost:5000/api/admin/student-course', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          student_id: selectedStudent,
          course_id: selectedCourse,
        }),
      });

      if (response.ok) {
        // ÿ™ÿ≠ÿØŸäÿ´ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ≥ÿ¨ŸÑÿ©
        const updatedCourses = [...studentCourses, { name: selectedCourse }];
        setStudentCourses(updatedCourses);
      } else {
        console.error('Failed to add course');
      }
    } catch (error) {
      console.error('Error adding course:', error);
    } finally {
      setAdding(false);
    }
  };

  const handleDeleteCourse = async (courseId) => {
    // ŸáŸÜÿß ŸäŸÖŸÉŸÜŸÉ ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸÜÿ∑ŸÇ ŸÑÿ≠ÿ∞ŸÅ ŸÖÿßÿØÿ© ŸÖŸÜ ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ≥ÿ¨ŸÑÿ©
  };

  return (
    <Container className="mt-4">
      <Card className="shadow-sm border-0">
        <Card.Header className="bg-primary text-white p-3">
          <h5 className="fw-bold m-0">ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸàÿßÿØ ŸÑŸÑÿ∑ŸÑÿßÿ®</h5>
        </Card.Header>
        <Card.Body>
          {/* ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ∑ÿßŸÑÿ® ŸàÿßŸÑŸÖÿßÿØÿ© */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>ÿßÿÆÿ™ÿ± ÿßŸÑÿ∑ÿßŸÑÿ®</Form.Label>
                <Form.Select
                  value={selectedStudent}
                  onChange={(e) => setSelectedStudent(e.target.value)}
                >
                  <option value="">-- ÿßÿÆÿ™ÿ± ÿ∑ÿßŸÑÿ®ÿßŸã --</option>
                  {students.map(student => (
                    <option key={student.id} value={student.id}>
                      {student.full_name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group>
                <Form.Label>ÿßÿÆÿ™ÿ± ÿßŸÑŸÖÿßÿØÿ©</Form.Label>
                <Form.Select
                  value={selectedCourse}
                  onChange={(e) => setSelectedCourse(e.target.value)}
                >
                  <option value="">-- ÿßÿÆÿ™ÿ± ŸÖÿßÿØÿ© --</option>
                  {courses.map(course => (
                    <option key={course.id} value={course.id}>
                      {course.name} - {course.department} ({course.credits} ÿ≥ÿßÿπÿßÿ™)
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col className="d-flex justify-content-end">
              <Button
                variant="success"
                onClick={handleAddCourse}
                disabled={adding || !selectedStudent || !selectedCourse}
              >
                {adding ? <Spinner animation="border" size="sm" /> : '‚ûï ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸÖÿßÿØÿ©'}
              </Button>
            </Col>
          </Row>
          <hr />
          <h5 className="mb-3">ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ≥ÿ¨ŸÑÿ© ŸÑŸÑÿ∑ÿßŸÑÿ®</h5>
          {loading ? (
            <div className="text-center my-4">
              <Spinner animation="border" variant="primary" />
            </div>
          ) : studentCourses.length > 0 ? (
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿßÿØÿ©</th>
                  <th>ÿßŸÑŸÇÿ≥ŸÖ</th>
                  <th>ÿßŸÑÿ≥ÿßÿπÿßÿ™</th>
                  <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                </tr>
              </thead>
              <tbody>
                {studentCourses.map(course => (
                  <tr key={course.id}>
                    <td>{course.name}</td>
                    <td>{course.department}</td>
                    <td>{course.credits} ÿ≥ÿßÿπÿßÿ™</td>
                    <td>
                      <Button variant="danger" onClick={() => handleDeleteCourse(course.id)}>
                        üóëÔ∏è ÿ≠ÿ∞ŸÅ
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p className="text-center text-muted">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸàÿßÿØ ŸÖÿ≥ÿ¨ŸÑÿ© ŸÑŸáÿ∞ÿß ÿßŸÑÿ∑ÿßŸÑÿ®</p>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default AddStudentCourses;
