import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import AdminDashboard from './admin/pages/AdminDashboard'; // تأكد من المسار الصحيح
import LoginPage from './admin/pages/LoginPage'; // تأكد من المسار الصحيح
import StudentDashboard from './student/pages/StudentDashboard'; // تأكد من المسار الصحيح

// دالة للتحقق من التوثيق
const isAuthenticated = () => {
  const token = localStorage.getItem('jwt_token');
  console.log(token)
  return token !== null;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={isAuthenticated() ? <Navigate to="/admin-dashboard" /> : <LoginPage />} />
        <Route path="/admin-dashboard" element={isAuthenticated() ? <AdminDashboard /> : <Navigate to="/" />} />
        <Route path="/Student-dashboard" element={isAuthenticated() ? <StudentDashboard /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
