import React, { useState, useEffect } from 'react';
import { Card, Container, Row, Col, Button, Spinner } from 'react-bootstrap';
import axios from 'axios';

function ViewStudentCourses() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStudentCourses();
  }, []);

  const fetchStudentCourses = () => {
    const studentId = 21; // استخدم الطالب المحدد، في هذه الحالة 21 كما في الرابط
    axios
      .get(`http://localhost:5000/api/student/courses-student/${studentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching student courses', error);
        setLoading(false);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">دورات الطالب</h5>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : courses.length > 0 ? (
                courses.map((course) => (
                  <Card key={course.id} className="mb-3 p-3 shadow-sm border-0">
                    <Row className="align-items-center">
                      <Col md={3}>
                        <h6 className="fw-bold">{course.name}</h6>
                      </Col>
                      <Col md={3}>
                        <p className="mb-1">{course.department}</p>
                      </Col>
                      <Col md={2}>
                        <p className="mb-1">{course.credits} ساعات</p>
                      </Col>
                      <Col md={3} className="d-flex gap-2">
                      </Col>
                    </Row>
                  </Card>
                ))
              ) : (
                <p className="text-center text-muted">لا توجد دورات للطالب</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ViewStudentCourses;
