import React, { useState, useEffect } from 'react';
import { Card, Container, Row, Col, Table, Spinner, Button, Form } from 'react-bootstrap';
import axios from 'axios';

function AddStudentCourses() {
  // ÿßÿ≥ÿ™ÿ±ÿ¨ÿßÿπ student_id ŸÖŸÜ localStorage
  const studentId = localStorage.getItem('student_id');
  const [studentCourses, setStudentCourses] = useState([]);
  const [allCourses, setAllCourses] = useState([]); // ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ•ÿ∂ÿßŸÅÿ©
  const [loading, setLoading] = useState(true);
  const [adding, setAdding] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState('');

  // ÿ¨ŸÑÿ® ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑÿ™Ÿä ÿ≥ÿ¨ŸÑŸáÿß ÿßŸÑÿ∑ÿßŸÑÿ®
  const fetchStudentCourses = () => {
    setLoading(true);
    axios
      .get(`http://localhost:5000/api/student/courses-student/${studentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setStudentCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching student courses', error);
        setLoading(false);
      });
  };

  // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© (ŸÜŸÅÿ™ÿ±ÿ∂ Ÿàÿ¨ŸàÿØ API ŸäÿπŸäÿØ ÿ¨ŸÖŸäÿπ ÿßŸÑÿØŸàÿ±ÿßÿ™)
  const fetchAllCourses = () => {
    axios
      .get(`http://localhost:5000/api/admin/courses`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setAllCourses(response.data);
      })
      .catch((error) => {
        console.error('Error fetching all courses', error);
      });
  };

  useEffect(() => {
    if (studentId) {
      fetchStudentCourses();
      fetchAllCourses();
    }
  }, [studentId]);

  // ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿßÿØÿ© ŸÑŸÑÿ∑ÿßŸÑÿ®
  const handleAddCourse = () => {
    if (!selectedCourse) return;
    setAdding(true);
    axios
      .post(
        'http://localhost:5000/api/student-course',
        { student_id: studentId, course_id: selectedCourse },
        { headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` } }
      )
      .then((response) => {
        fetchStudentCourses();
        setAdding(false);
        setSelectedCourse('');
      })
      .catch((error) => {
        console.error('Error adding course', error);
        setAdding(false);
      });
  };

  // ÿ≠ÿ∞ŸÅ ŸÖÿßÿØÿ© ŸÑŸÑÿ∑ÿßŸÑÿ® (Ÿäÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ≠ÿ∞ŸÅ ŸÅŸä body ÿπÿ®ÿ± axios.delete)
  const handleDeleteCourse = (courseId) => {
    axios
      .delete('http://localhost:5000/api/student-course', {
        data: { student_id: studentId, course_id: courseId },
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        fetchStudentCourses();
      })
      .catch((error) => {
        console.error('Error deleting course', error);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">ÿ•ÿØÿßÿ±ÿ© ŸÖŸàÿßÿØ ÿßŸÑÿ∑ÿßŸÑÿ®</h5>
            </Card.Header>
            <Card.Body>
              {/* ŸÇÿ≥ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿßÿØÿ© */}
              <Card className="mb-3 p-3 shadow-sm border-0 bg-light">
                <Row className="align-items-center">
                  <Col md={8}>
                    <Form.Select
                      value={selectedCourse}
                      onChange={(e) => setSelectedCourse(e.target.value)}
                    >
                      <option value="">ÿßÿÆÿ™ÿ± ŸÖÿßÿØÿ© ŸÑÿ•ÿ∂ÿßŸÅÿ™Ÿáÿß</option>
                      {allCourses.map((course) => (
                        <option key={course.id} value={course.id}>
                          {course.name} - {course.department} ({course.credits} ÿ≥ÿßÿπÿßÿ™)
                        </option>
                      ))}
                    </Form.Select>
                  </Col>
                  <Col md={4} className="d-flex gap-2">
                    <Button variant="success" onClick={handleAddCourse} disabled={adding}>
                      {adding ? <Spinner animation="border" size="sm" /> : '‚ûï ÿ•ÿ∂ÿßŸÅÿ©'}
                    </Button>
                  </Col>
                </Row>
              </Card>
              {/* ÿπÿ±ÿ∂ ÿßŸÑŸÖŸàÿßÿØ ÿßŸÑŸÖÿ∂ÿßŸÅÿ© ŸÑŸÑÿ∑ÿßŸÑÿ® */}
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : studentCourses.length > 0 ? (
                <Table striped bordered hover className="mt-3">
                  <thead>
                    <tr>
                      <th>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿßÿØÿ©</th>
                      <th>ÿßŸÑŸÇÿ≥ŸÖ</th>
                      <th>ÿßŸÑÿ≥ÿßÿπÿßÿ™</th>
                      <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
                    </tr>
                  </thead>
                  <tbody>
                    {studentCourses.map((course) => (
                      <tr key={course.id}>
                        <td>{course.name}</td>
                        <td>{course.department}</td>
                        <td>{course.credits} ÿ≥ÿßÿπÿßÿ™</td>
                        <td>
                          <Button variant="danger" onClick={() => handleDeleteCourse(course.id)}>
                            üóëÔ∏è ÿ≠ÿ∞ŸÅ
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              ) : (
                <p className="text-center text-muted">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÖŸàÿßÿØ ŸÖÿ≥ÿ¨ŸÑÿ© ŸÑŸÑÿ∑ÿßŸÑÿ®</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AddStudentCourses;
