import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner, Table } from 'react-bootstrap';
import axios from 'axios';

function ManageGrades() {
  const [courses, setCourses] = useState([]);
  const [students, setStudents] = useState([]);
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedStudentId, setSelectedStudentId] = useState('');
  const [studentCourses, setStudentCourses] = useState([]);
  const [selectedCourseId, setSelectedCourseId] = useState('');
  const [midterm, setMidterm] = useState('');
  const [final, setFinal] = useState('');
  const [selectedGradeId, setSelectedGradeId] = useState(null);

  useEffect(() => {
    fetchStudents();
    fetchGrades();
  }, []);

  const fetchStudents = () => {
    axios.get('http://localhost:5000/api/admin/students', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => setStudents(response.data))
    .catch((error) => console.error('Error fetching students', error));
  };

  const fetchStudentCourses = (studentId) => {
    if (!studentId) return;
    axios.get(`http://localhost:5000/api/admin/student-courses/${studentId}`, {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => setStudentCourses(response.data))
    .catch((error) => console.error('Error fetching student courses', error));
  };

  const fetchGrades = () => {
    setLoading(true);
    axios.get('http://localhost:5000/api/admin/grades', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => {
      setGrades(response.data);
      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching grades', error);
      setLoading(false);
    });
  };

  const handleAddOrUpdateGrade = () => {
    if (!midterm || !final) {
      alert('يرجى ملء جميع الحقول');
      return;
    }

    const gradeData = {
      student_id: selectedStudentId,
      course_id: selectedCourseId,
      midterm,
      final,
      total: parseInt(midterm) + parseInt(final),
      status: parseInt(midterm) + parseInt(final) >= 50 ? 'Pass' : 'Fail',
    };

    if (selectedGradeId) {
      axios.put(`http://localhost:5000/api/admin/grade/${selectedGradeId}`, gradeData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchGrades();
        resetForm();
      })
      .catch((error) => console.error('Error updating grade', error));
    } else {
      axios.post('http://localhost:5000/api/admin/grade', gradeData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchGrades();
        resetForm();
      })
      .catch((error) => console.error('Error adding grade', error));
    }
  };

  const resetForm = () => {
    setMidterm('');
    setFinal('');
    setSelectedStudentId('');
    setSelectedCourseId('');
    setStudentCourses([]);
    setSelectedGradeId(null);
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white p-3">
              <h5 className="fw-bold m-0">{selectedGradeId ? 'تعديل درجة' : 'إضافة درجة جديدة'}</h5>
            </Card.Header>
            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  <Form>
                    <Form.Group>
                      <Form.Label>اختر الطالب</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedStudentId}
                        onChange={(e) => {
                          setSelectedStudentId(e.target.value);
                          fetchStudentCourses(e.target.value);
                        }}
                      >
                        <option value="">اختر الطالب</option>
                        {students.map((student) => (
                          <option key={student.id} value={student.id}>
                            {student.full_name}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>اختر المادة</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedCourseId}
                        onChange={(e) => setSelectedCourseId(e.target.value)}
                      >
                        <option value="">اختر المادة</option>
                        {studentCourses.map((course) => (
                          <option key={course.id} value={course.id}>
                            {course.name}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>درجة الامتحان النصفي</Form.Label>
                      <Form.Control type="number" value={midterm} onChange={(e) => setMidterm(e.target.value)} />
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>درجة الامتحان النهائي</Form.Label>
                      <Form.Control type="number" value={final} onChange={(e) => setFinal(e.target.value)} />
                    </Form.Group>

                    <Button variant="success" onClick={handleAddOrUpdateGrade} className="mt-3">
                      {selectedGradeId ? 'تعديل درجة' : 'إضافة درجة'}
                    </Button>
                  </Form>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageGrades;
