import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const PrivateRoute = ({ element: Element, requiredRole, ...rest }) => {
  const [isAuthorized, setIsAuthorized] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // التحقق من التوكن والدور من السيرفر باستخدام الكوكي
    axios.get("http://localhost:5000/api/auth/verify", {
      withCredentials: true,
    })
    .then((response) => {
      setUserRole(response.data.role); // استرجاع الدور من الاستجابة
      setIsAuthorized(true);
      setLoading(false);
    })
    .catch(() => {
      setIsAuthorized(false);
      setLoading(false);
    });
  }, []);

  if (loading) return null; // يمكن إضافة مكون تحميل هنا

  if (!isAuthorized) {
    return <Navigate to="/" />;
  }

  if (requiredRole && requiredRole !== userRole) {
    if (userRole === "admin") {
      return <Navigate to="/admin-dashboard" />;
    } else if (userRole === "student") {
      return <Navigate to="/student-dashboard" />;
    }
  }

  return <Element {...rest} />;
};

export default PrivateRoute;
