import React from "react";
import { Card, Button, Container, Row, Col } from "react-bootstrap";

function AdminProfile() {
  // بيانات افتراضية
  const adminData = {
    name: "John Doe",
    email: "admin@example.com",
    role: "Administrator",
    phone: "+123 456 7890",
    address: "123 Admin Street, City, Country",
    profilePic: "https://via.placeholder.com/150", // صورة افتراضية
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <Card className="shadow-lg rounded-4 p-3">
            <Card.Body className="text-center">
              <img
                src={adminData.profilePic}
                alt="Profile"
                className="rounded-circle mb-3"
                width={120}
                height={120}
              />
              <h3 className="fw-bold">{adminData.name}</h3>
              <p className="text-muted">{adminData.role}</p>
              <hr />
              <p><strong>Email:</strong> {adminData.email}</p>
              <p><strong>Phone:</strong> {adminData.phone}</p>
              <p><strong>Address:</strong> {adminData.address}</p>
              <Button variant="primary" className="mt-3">Edit Profile</Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
