import React, { useState } from 'react';
import { Button, Nav } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import ManageStudents from './ManageStudents';
import AdminProfile from './AdminProfile';
import ManageCourses from './ManageCourses';
import ManageGrades from './ManageGrades';
import AddStudentCourses from './Add-student-courses';
import AddAdmin from './AddAdmin';

function AdminDashboard() {
  const [activeComponent, setActiveComponent] = useState('profile');
  const navigate = useNavigate();

  // وظيفة تسجيل الخروج
  const handleLogout = () => {
    localStorage.removeItem('jwt_token');
    localStorage.removeItem('student_id');
    localStorage.removeItem('role');
    navigate('/');
  };

  // تحديد المكون النشط بناءً على الاختيار
  const renderComponent = () => {
    switch (activeComponent) {
      case 'profile':
        return <AdminProfile />;
      case 'students':
        return <ManageStudents />;
      case 'courses':
        return <ManageCourses />;
      case 'grades':
        return <ManageGrades />;
      case 'student-courses':
        return <AddStudentCourses />;
      case 'add-admin':
        return <AddAdmin />;
      default:
        return <AdminProfile />;
    }
  };

  return (
    <div className="d-flex">
      {/* الشريط الجانبي */}
      <div className="bg-dark text-white p-3 vh-100" style={{ width: '250px' }}>
        <h3 className="text-center mb-4">لوحة التحكم</h3>
        <Nav className="flex-column">
          <Nav.Link onClick={() => setActiveComponent('profile')} className="text-white">معلومات الأدمن</Nav.Link>
          <Nav.Link onClick={() => setActiveComponent('students')} className="text-white">إدارة الطلاب</Nav.Link>
          <Nav.Link onClick={() => setActiveComponent('courses')} className="text-white">إدارة المواد</Nav.Link>
          <Nav.Link onClick={() => setActiveComponent('grades')} className="text-white">إدارة العلامات</Nav.Link>
          <Nav.Link onClick={() => setActiveComponent('student-courses')} className="text-white">إضافة مواد للطالب</Nav.Link>
          <Nav.Link onClick={() => setActiveComponent('add-admin')} className="text-white">إضافة أدمن</Nav.Link>
          <hr className="bg-white" />
          <Button variant="danger" onClick={handleLogout} className="mt-3">تسجيل الخروج</Button>
        </Nav>
      </div>

      {/* محتوى الصفحة */}
      <div className="flex-grow-1 p-4">
        {renderComponent()}
      </div>
    </div>
  );
}

export default AdminDashboard;
