import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner, Modal } from 'react-bootstrap';
import axios from 'axios';

function ManageCourses() {
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [newCourse, setNewCourse] = useState({
    name: '',
    department: '',
    credits: '',
  });
  const [showAddModal, setShowAddModal] = useState(false);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = () => {
    axios
      .get('http://localhost:5000/api/admin/courses', {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching courses', error);
        setLoading(false);
      });
  };

  const handleAddCourse = () => {
    axios
      .post('http://localhost:5000/api/admin/add-course', newCourse, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        setShowAddModal(false);
        fetchCourses();
        setNewCourse({
          name: '',
          department: '',
          credits: '',
        });
      })
      .catch((error) => console.error('Error adding course', error));
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">إدارة المواد</h5>
              <Form.Control
                type="text"
                placeholder="🔍 بحث عن مادة..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-50 mx-2"
              />
              <Button variant="light" onClick={() => setShowAddModal(true)}>
                ➕ إضافة مادة
              </Button>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : courses.length > 0 ? (
                courses
                  .filter((course) =>
                    course.name.toLowerCase().includes(searchTerm.toLowerCase())
                  )
                  .map((course) => (
                    <Card key={course.id} className="mb-3 p-3 shadow-sm border-0">
                      <Row className="align-items-center">
                        <Col md={3}>
                          <h6 className="fw-bold">{course.name}</h6>
                        </Col>
                        <Col md={2}>
                          <p className="mb-1">{course.department}</p>
                        </Col>
                        <Col md={2}>
                          <p className="mb-1">{course.credits} ساعات</p>
                        </Col>
                        <Col md={2}>
                          <p className="mb-1 text-muted">
                            تم الإنشاء في: {new Date(course.created_at).toLocaleString()}
                          </p>
                        </Col>
                      </Row>
                    </Card>
                  ))
              ) : (
                <p className="text-center text-muted">لا يوجد مواد متاحة</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* نموذج إضافة المادة */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>إضافة مادة جديدة</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>اسم المادة</Form.Label>
              <Form.Control
                type="text"
                value={newCourse.name}
                onChange={(e) => setNewCourse({ ...newCourse, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>القسم</Form.Label>
              <Form.Control
                type="text"
                value={newCourse.department}
                onChange={(e) => setNewCourse({ ...newCourse, department: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>عدد الساعات المعتمدة</Form.Label>
              <Form.Control
                type="number"
                value={newCourse.credits}
                onChange={(e) => setNewCourse({ ...newCourse, credits: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            🔙 رجوع
          </Button>
          <Button variant="success" onClick={handleAddCourse}>
            ✅ إضافة
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default ManageCourses;
