import React, { useState, useEffect } from 'react';
import { Card, Button, Form } from 'react-bootstrap';
import axios from 'axios';

function AdminProfile() {
  const [adminData, setAdminData] = useState({
    fullName: '',
    email: '',
    phone: '',
    address: '',
  });

  const [loading, setLoading] = useState(true); // لعرض حالة التحميل
  const [error, setError] = useState(null); // لعرض الأخطاء إذا حدثت

  // تحميل البيانات من API عند تحميل الصفحة
  useEffect(() => {
    // جلب البيانات باستخدام axios
    axios.get('http://localhost:5000/api/admin/profile')
      .then(response => {
        setAdminData(response.data); // تخزين البيانات في الحالة
        setLoading(false); // إنهاء حالة التحميل
      })
      .catch(error => {
        setError('حدث خطأ أثناء تحميل البيانات');
        setLoading(false); // إنهاء حالة التحميل
      });
  }, []);

  if (loading) {
    return <div>جاري تحميل البيانات...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mt-4">
      <Card className="shadow-lg">
        <Card.Header as="h3" className="bg-primary text-white text-center">
          معلومات الأدمن الشخصية
        </Card.Header>
        <Card.Body>
          <h5>البيانات الحالية:</h5>
          <Form>
            <Form.Group controlId="formFullName" className="mb-3">
              <Form.Label>الاسم الكامل</Form.Label>
              <Form.Control
                type="text"
                placeholder="أدخل اسمك الكامل"
                value={adminData.fullName}
                readOnly
              />
            </Form.Group>

            <Form.Group controlId="formEmail" className="mb-3">
              <Form.Label>البريد الإلكتروني</Form.Label>
              <Form.Control
                type="email"
                placeholder="أدخل بريدك الإلكتروني"
                value={adminData.email}
                readOnly
              />
            </Form.Group>

            <Form.Group controlId="formPhone" className="mb-3">
              <Form.Label>رقم الهاتف</Form.Label>
              <Form.Control
                type="text"
                placeholder="أدخل رقم هاتفك"
                value={adminData.phone}
                readOnly
              />
            </Form.Group>

            <Form.Group controlId="formAddress" className="mb-3">
              <Form.Label>العنوان</Form.Label>
              <Form.Control
                type="text"
                placeholder="أدخل عنوانك"
                value={adminData.address}
                readOnly
              />
            </Form.Group>

            <Button variant="primary" type="button" disabled>
              تعديل المعلومات
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </div>
  );
}

export default AdminProfile;
