import React, { useState } from "react";
import { Card, ListGroup, Button, Container, Row, Col, Form } from "react-bootstrap";

function AdminProfile() {
  // بيانات المستخدم (مثال)
  const [userInfo, setUserInfo] = useState({
    id: "12345",
    full_name: "John Doe",
    email: "admin@example.com",
    phone: "+123 456 7890",
    address: "123 Admin Street, City, Country",
  });

  // حالة لتبديل بين عرض البيانات أو تعديلها
  const [isEditing, setIsEditing] = useState(false);

  // حالة لتخزين البيانات المعدلة
  const [updatedInfo, setUpdatedInfo] = useState(userInfo);

  // دالة لتحديث البيانات المعدلة
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedInfo({
      ...updatedInfo,
      [name]: value,
    });
  };

  // دالة لتأكيد التعديلات
  const handleSave = () => {
    setUserInfo(updatedInfo);
    setIsEditing(false); // العودة إلى عرض التفاصيل
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={8}>
          <Card className="shadow-sm h-100">
            <Card.Header className="bg-primary text-white">
              {isEditing ? "تعديل تفاصيل الحساب" : "تفاصيل الحساب"}
            </Card.Header>

            <Card.Body>
              {isEditing ? (
                // نموذج التعديل
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Label>الاسم الكامل</Form.Label>
                    <Form.Control
                      type="text"
                      name="full_name"
                      value={updatedInfo.full_name}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>البريد الإلكتروني</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      value={updatedInfo.email}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>رقم الهاتف</Form.Label>
                    <Form.Control
                      type="text"
                      name="phone"
                      value={updatedInfo.phone}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>العنوان</Form.Label>
                    <Form.Control
                      type="text"
                      name="address"
                      value={updatedInfo.address}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Button variant="primary" onClick={handleSave}>
                    تأكيد
                  </Button>
                </Form>
              ) : (
                // عرض التفاصيل
                <ListGroup variant="flush">
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>الاسم الكامل:</strong>
                    <span>{userInfo.full_name}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>البريد الإلكتروني:</strong>
                    <span>{userInfo.email}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>رقم الهاتف:</strong>
                    <span>{userInfo.phone}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>العنوان:</strong>
                    <span>{userInfo.address}</span>
                  </ListGroup.Item>
                </ListGroup>
              )}
            </Card.Body>

            {!isEditing && (
              <Card.Footer className="text-center">
                <Button variant="outline-primary" onClick={() => setIsEditing(true)}>
                  تعديل الملف الشخصي
                </Button>
              </Card.Footer>
            )}
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
