import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Table, Form, Spinner } from 'react-bootstrap';
import axios from 'axios';

function ManageGrades() {
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingGradeId, setEditingGradeId] = useState(null);
  const [editedGrades, setEditedGrades] = useState({});

  useEffect(() => {
    fetchGrades();
  }, []);

  const fetchGrades = () => {
    setLoading(true);
    axios.get('http://localhost:5000/api/admin/grades')
      .then((response) => {
        setGrades(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching grades', error);
        setLoading(false);
      });
  };

  const handleEditClick = (grade) => {
    setEditingGradeId(grade.id);
    setEditedGrades({
      midterm: grade.midterm,
      final: grade.final,
    });
  };

  const handleChange = (e, field) => {
    setEditedGrades({ ...editedGrades, [field]: e.target.value });
  };

  const handleSave = (id) => {
    const updatedGrade = {
      midterm: editedGrades.midterm,
      final: editedGrades.final,
      total: parseInt(editedGrades.midterm) + parseInt(editedGrades.final),
      status: parseInt(editedGrades.midterm) + parseInt(editedGrades.final) >= 50 ? 'ناجح' : 'راسب',
    };

    axios.put(`http://localhost:5000/api/admin/grade/${id}`, updatedGrade)
      .then(() => {
        fetchGrades();
        setEditingGradeId(null);
      })
      .catch((error) => console.error('Error updating grade', error));
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white text-center">
              <h5 className="fw-bold m-0">إدارة العلامات</h5>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  <h6 className="mb-3">جميع الدرجات</h6>
                  <Table striped bordered hover className="mt-3 text-center">
                    <thead>
                      <tr>
                        <th>اسم الطالب</th>
                        <th>اسم المادة</th>
                        <th>درجة النصفي</th>
                        <th>درجة النهائي</th>
                        <th>الإجمالي</th>
                        <th>الحالة</th>
                        <th>الإجراءات</th>
                      </tr>
                    </thead>
                    <tbody>
                      {grades.map((grade) => (
                        <tr key={grade.id}>
                          <td>{grade.student_name}</td>
                          <td>{grade.course_name}</td>
                          <td>
                            {editingGradeId === grade.id ? (
                              <Form.Control type="number" value={editedGrades.midterm} onChange={(e) => handleChange(e, 'midterm')} />
                            ) : (
                              grade.midterm
                            )}
                          </td>
                          <td>
                            {editingGradeId === grade.id ? (
                              <Form.Control type="number" value={editedGrades.final} onChange={(e) => handleChange(e, 'final')} />
                            ) : (
                              grade.final
                            )}
                          </td>
                          <td>{editingGradeId === grade.id ? parseInt(editedGrades.midterm) + parseInt(editedGrades.final) : grade.total}</td>
                          <td>{editingGradeId === grade.id ? (parseInt(editedGrades.midterm) + parseInt(editedGrades.final) >= 50 ? "ناجح" : "راسب") : (grade.status === "Pass" ? "ناجح" : "راسب")}</td>
                          <td>
                            {editingGradeId === grade.id ? (
                              <Button variant="success" size="sm" onClick={() => handleSave(grade.id)}>حفظ</Button>
                            ) : (
                              <Button variant="primary" size="sm" onClick={() => handleEditClick(grade)}>تعديل</Button>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageGrades;
