import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner, Modal } from 'react-bootstrap';
import axios from 'axios';

function ManageCourses() {
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [editCourse, setEditCourse] = useState({ name: '', department: '', credits: '' });
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = () => {
    axios
      .get('http://localhost:5000/api/admin/courses', {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching courses', error);
        setLoading(false);
      });
  };

  const handleEditClick = (course) => {
    setSelectedCourse(course);
    setEditCourse({ name: course.name, department: course.department, credits: course.credits });
    setShowEditModal(true);
  };

  const handleUpdateCourse = () => {
    axios
      .put(`http://localhost:5000/api/admin/course/${selectedCourse.id}`, editCourse, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchCourses();
        setShowEditModal(false);
      })
      .catch((error) => console.error('Error updating course', error));
  };

  const handleDeleteCourse = () => {
    axios
      .delete(`http://localhost:5000/api/admin/course/${selectedCourse.id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchCourses();
        setShowEditModal(false);
      })
      .catch((error) => console.error('Error deleting course', error));
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸàÿßÿØ</h5>
              <Form.Control
                type="text"
                placeholder="üîç ÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖÿßÿØÿ©..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-50 mx-2"
              />
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : courses.length > 0 ? (
                courses
                  .filter((course) =>
                    course.name.toLowerCase().includes(searchTerm.toLowerCase())
                  )
                  .map((course) => (
                    <Card key={course.id} className="mb-3 p-3 shadow-sm border-0">
                      <Row className="align-items-center">
                        <Col md={3}>
                          <h6 className="fw-bold">{course.name}</h6>
                        </Col>
                        <Col md={3}>
                          <p className="mb-1">{course.department}</p>
                        </Col>
                        <Col md={3}>
                          <p className="mb-1">{course.credits} ÿ≥ÿßÿπÿßÿ™</p>
                        </Col>
                        <Col md={3} className="text-end">
                          <Button variant="warning" onClick={() => handleEditClick(course)}>
                            ‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ
                          </Button>
                        </Col>
                      </Row>
                    </Card>
                  ))
              ) : (
                <p className="text-center text-muted">ŸÑÿß ŸäŸàÿ¨ÿØ ŸÖŸàÿßÿØ ŸÖÿ™ÿßÿ≠ÿ©</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* ŸÜŸÖŸàÿ∞ÿ¨ ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖÿßÿØÿ© */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖÿßÿØÿ©</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>ÿßÿ≥ŸÖ ÿßŸÑŸÖÿßÿØÿ©</Form.Label>
              <Form.Control
                type="text"
                value={editCourse.name}
                onChange={(e) => setEditCourse({ ...editCourse, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>ÿßŸÑŸÇÿ≥ŸÖ</Form.Label>
              <Form.Control
                type="text"
                value={editCourse.department}
                onChange={(e) => setEditCourse({ ...editCourse, department: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>ÿπÿØÿØ ÿßŸÑÿ≥ÿßÿπÿßÿ™ ÿßŸÑŸÖÿπÿ™ŸÖÿØÿ©</Form.Label>
              <Form.Control
                type="number"
                value={editCourse.credits}
                onChange={(e) => setEditCourse({ ...editCourse, credits: e.target.value })}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={handleDeleteCourse}>
            üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿßŸÑŸÉŸàÿ±ÿ≥
          </Button>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            üîô ÿ±ÿ¨Ÿàÿπ
          </Button>
          <Button variant="success" onClick={handleUpdateCourse}>
            ‚úÖ ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ÿπÿØŸäŸÑ
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default ManageCourses;
