import React from 'react';
import { Navigate } from 'react-router-dom';

// مكون حماية المسار
const PrivateRoute = ({ element: Element, ...rest }) => {
  const token = localStorage.getItem('token'); // تحقق من وجود الـ token في localStorage

  if (!token) {
    // إذا لم يوجد الـ token، أعد توجيه المستخدم إلى صفحة تسجيل الدخول
    return <Navigate to="/" />;
  }

  // إذا كان الـ token موجودًا، يعرض المكون المحدد
  return <Element {...rest} />;
};

export default PrivateRoute;
