import React, { useState } from 'react';
import { Tab, Tabs, Dropdown, DropdownButton } from 'react-bootstrap';
import ManageStudents from './ManageStudents';
import AdminProfile from './AdminProfile';
import ManageCourses from './ManageCourses';
import ManageGrades from './ManageGrades';
import ManageAttendance from './ManageAttendance';
import AccountSettings from './AccountSettings';

function AdminDashboard() {
  const [key, setKey] = useState('profile');

  // دالة لتسجيل الخروج
  const handleLogout = () => {
    // مسح التوكن من localStorage
    localStorage.removeItem('jwt_token');
    // التوجيه إلى صفحة تسجيل الدخول
    window.location.href = '/login'; // أو استخدم navigate إذا كنت تستخدم react-router
  };

  return (
    <div className="container mt-4">
      <Tabs activeKey={key} onSelect={(k) => setKey(k)} className="mb-3">
        <Tab eventKey="profile" title="معلومات الأدمن">
          <AdminProfile />
        </Tab>
        <Tab eventKey="students" title="إدارة الطلاب">
          <ManageStudents />
        </Tab>
        <Tab eventKey="courses" title="إدارة المواد">
          <ManageCourses />
        </Tab>
        <Tab eventKey="grades" title="إدارة العلامات">
          <ManageGrades />
        </Tab>
        <Tab eventKey="attendance" title="إدارة الحضور">
          <ManageAttendance />
        </Tab>
        <Tab eventKey="settings" title="إعدادات الحساب">
          <AccountSettings />
        </Tab>
      </Tabs>
      
      {/* زر تسجيل الخروج */}
      <DropdownButton 
        variant="link" 
        id="logout-dropdown" 
        title="تسجيل الخروج" 
        className="position-absolute end-0" 
        onSelect={handleLogout}
      >
        <Dropdown.Item eventKey="logout" onClick={handleLogout}>
          تأكيد تسجيل الخروج
        </Dropdown.Item>
      </DropdownButton>
    </div>
  );
}

export default AdminDashboard;
