import React, { useState, useEffect } from 'react';
import { Card, Container, Row, Col, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

// دالة لاستخراج studentId من التوكن
const getStudentIdFromToken = () => {
  const token = localStorage.getItem('jwt_token');
  if (token) {
    
    const decodedToken = jwtDecode(token);

    return decodedToken.studentId; // تأكد أن هذا هو المفتاح الصحيح في الـ token
  }
  return null;
};

function ViewCourses() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const studentId = getStudentIdFromToken();  // استخراج studentId من التوكن
    if (studentId) {
      fetchCourses(studentId);  // إذا تم استخراج studentId، قم بتحميل المواد الخاصة بالطالب
    }
  }, []);

  const fetchCourses = (studentId) => {
    axios
      .get(`http://localhost:5000/api/student/courses-student/${studentId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching courses', error);
        setLoading(false);
      });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white p-3">
              <h5 className="fw-bold m-0">المواد المسجلة</h5>
            </Card.Header>
            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : courses.length > 0 ? (
                courses.map((course) => (
                  <Card key={course.id} className="mb-3 p-3 shadow-sm border-0">
                    <Row className="align-items-center">
                      <Col md={4}>
                        <h6 className="fw-bold">{course.name}</h6>
                      </Col>
                      <Col md={4}>
                        <p className="mb-1">القسم: {course.department}</p>
                      </Col>
                      <Col md={4}>
                        <p className="mb-1">الساعات: {course.credits}</p>
                      </Col>
                    </Row>
                  </Card>
                ))
              ) : (
                <p className="text-center text-muted">لا يوجد مواد مسجلة</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ViewCourses;
