import React, { useState, useEffect } from "react";
import { Card, ListGroup, Button, Container, Row, Col, Form } from "react-bootstrap";
import axios from "axios";

function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/admin/students", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setStudents(response.data);
      })
      .catch((error) => {
        console.error("Error fetching students", error);
      });
  }, []);

  const filteredStudents = students.filter((student) =>
    student.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.phone.includes(searchTerm)
  );

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm h-100">
            <Card.Header className="bg-primary text-white d-flex justify-content-between">
              <span>إدارة الطلاب</span>
              <Form.Control
                type="text"
                placeholder="بحث عن طالب..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-50"
              />
            </Card.Header>

            <Card.Body>
              <ListGroup variant="flush">
                {filteredStudents.length > 0 ? (
                  filteredStudents.map((student) => (
                    <ListGroup.Item key={student.id} className="d-flex justify-content-between">
                      <div>
                        <strong>{student.full_name}</strong> <br />
                        <span>{student.email}</span> <br />
                        <span>{student.phone}</span> <br />
                        <span className={`badge bg-${student.status === "نشط" ? "success" : "danger"}`}>
                          {student.status}
                        </span>
                      </div>
                      <Button variant="outline-primary" size="sm">تعديل</Button>
                    </ListGroup.Item>
                  ))
                ) : (
                  <p className="text-center">لا يوجد طلاب متاحين</p>
                )}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageStudents;
