import React, { useState, useEffect } from "react";
import { Card, ListGroup, Button, Container, Row, Col, Form } from "react-bootstrap";
import axios from "axios";

function AdminProfile() {
  const [userInfo, setUserInfo] = useState(null);  // بيانات المستخدم
  const [updatedInfo, setUpdatedInfo] = useState(null);  // البيانات المعدلة
  const [isEditing, setIsEditing] = useState(false);  // لتبديل الوضع بين عرض البيانات وتعديلها

  // تحميل البيانات عند تحميل الصفحة
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/admin/profile", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setUserInfo(response.data);
        setUpdatedInfo(response.data); // نسخ البيانات الأصلية للـ updatedInfo
      })
      .catch((error) => {
        console.error("Error fetching profile data", error);
        // يمكن هنا إضافة رسائل خطأ إذا أردت
      });
  }, []);

  // تغيير البيانات عند التعديل
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedInfo({
      ...updatedInfo,
      [name]: value,
    });
  };

  // حفظ البيانات المعدلة
  const handleSave = () => {
    axios
      .put("http://localhost:5000/api/admin/profile", updatedInfo, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setUserInfo(updatedInfo); // تحديث البيانات المعروضة بعد التعديل
        setIsEditing(false); // العودة للوضع العرض
      })
      .catch((error) => {
        console.error("Error saving profile data", error);
        // يمكن هنا إضافة رسائل خطأ إذا أردت
      });
  };

  if (!userInfo) {
    return <div>Loading...</div>;  // عرض رسالة تحميل أثناء جلب البيانات
  }

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={8}>
          <Card className="shadow-sm h-100">
            <Card.Header className="bg-primary text-white">
              {isEditing ? "تعديل تفاصيل الحساب" : "تفاصيل الحساب"}
            </Card.Header>

            <Card.Body>
              {isEditing ? (
                // نموذج تعديل البيانات
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Label>الاسم الكامل</Form.Label>
                    <Form.Control
                      type="text"
                      name="full_name"
                      value={updatedInfo.full_name}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>البريد الإلكتروني</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      value={updatedInfo.email}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>رقم الهاتف</Form.Label>
                    <Form.Control
                      type="text"
                      name="phone"
                      value={updatedInfo.phone}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3">
                    <Form.Label>العنوان</Form.Label>
                    <Form.Control
                      type="text"
                      name="address"
                      value={updatedInfo.address}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <div className="mx-2">
                  <Button variant="primary" onClick={handleSave}>
                    تأكيد
                  </Button>
                  <Button variant="primary" onClick={handleSave}>
                    تأكيد
                  </Button>
                  </div>
                </Form>
              ) : (
                // عرض البيانات
                <ListGroup variant="flush">
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>الاسم الكامل:</strong>
                    <span>{userInfo.full_name}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>البريد الإلكتروني:</strong>
                    <span>{userInfo.email}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>رقم الهاتف:</strong>
                    <span>{userInfo.phone}</span>
                  </ListGroup.Item>
                  <ListGroup.Item className="d-flex justify-content-between">
                    <strong>العنوان:</strong>
                    <span>{userInfo.address}</span>
                  </ListGroup.Item>
                </ListGroup>
              )}
            </Card.Body>

            {!isEditing && (
              <Card.Footer className="text-center">
                <Button variant="outline-primary" onClick={() => setIsEditing(true)}>
                  تعديل الملف الشخصي
                </Button>
              </Card.Footer>
            )}
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
