import React, { useState } from 'react';
import { Tab, Tabs, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import ManageStudents from './ManageStudents';
import AdminProfile from './AdminProfile';
import ManageCourses from './ManageCourses';
import ManageGrades from './ManageGrades';
import ManageAttendance from './ManageAttendance';
import AccountSettings from './AccountSettings';

function AdminDashboard() {
  const [key, setKey] = useState('profile');
  const navigate = useNavigate();

  // وظيفة تسجيل الخروج
  const handleLogout = () => {
    // حذف التوكن من localStorage
    localStorage.removeItem('jwt_token');
    // إعادة التوجيه إلى صفحة تسجيل الدخول
    navigate('/');
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between">
        <h2>لوحة التحكم</h2>
        <Button variant="danger" onClick={handleLogout}>تسجيل الخروج</Button>
      </div>
      
      <Tabs activeKey={key} onSelect={(k) => setKey(k)} className="mb-3">
        <Tab eventKey="profile" title="معلومات الأدمن">
          <AdminProfile />
        </Tab>
        <Tab eventKey="students" title="إدارة الطلاب">
          <ManageStudents />
        </Tab>
        <Tab eventKey="courses" title="إدارة المواد">
          <ManageCourses />
        </Tab>
        <Tab eventKey="grades" title="إدارة العلامات">
          <ManageGrades />
        </Tab>
        
        <Tab eventKey="settings" title="إعدادات الحساب">
          <AccountSettings />
        </Tab>
      </Tabs>
    </div>
  );
}

export default AdminDashboard;
