import React, { useState, useEffect } from "react";
import { Card, ListGroup, Button, Container, Row, Col, Form, Spinner } from "react-bootstrap";
import axios from "axios";

function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/admin/students", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setStudents(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching students", error);
        setLoading(false);
      });
  }, []);

  const filteredStudents = students.filter((student) =>
    student.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.phone.includes(searchTerm)
  );

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
              <span className="fw-bold">إدارة الطلاب</span>
              <Form.Control
                type="text"
                placeholder="بحث عن طالب..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-50"
              />
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : filteredStudents.length > 0 ? (
                <ListGroup variant="flush">
                  {filteredStudents.map((student) => (
                    <ListGroup.Item key={student.id} className="d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="mb-1">{student.full_name}</h6>
                        <small className="text-muted">{student.email}</small> <br />
                        <small className="text-muted">{student.phone}</small> <br />
                        <span className={`badge bg-${student.status === "نشط" ? "success" : "danger"}`}>
                          {student.status}
                        </span>
                      </div>
                      <Button variant="outline-primary" size="sm">تعديل</Button>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              ) : (
                <p className="text-center text-muted">لا يوجد طلاب متاحين</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageStudents;
