import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminProfile() {
  const [userInfo, setUserInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // الحصول على التوكن من localStorage
    const token = localStorage.getItem('jwt_token');
    
    // إذا كان هناك توكن، قم بإرسال الطلب
    if (token) {
      axios.get('http://localhost:5000/api/admin/profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
      .then((response) => {
        // تعيين بيانات المستخدم
        setUserInfo(response.data);
      })
      .catch((err) => {
        setError('فشل في جلب البيانات، تأكد من صحة التوكن.');
      });
    } else {
      setError('لا يوجد توكن صالح.');
    }
  }, []);

  return (
    <div className="container">
      {error && <div className="alert alert-danger">{error}</div>}
      {userInfo ? (
        <div>
          <h3>معلومات المستخدم</h3>
          <ul>
            <li><strong>الاسم:</strong> {userInfo.full_name}</li>
            <li><strong>البريد الإلكتروني:</strong> {userInfo.email}</li>
            <li><strong>الهاتف :</strong> {userInfo.phone}</li>
          </ul>
        </div>
      ) : (
        <div>جاري تحميل البيانات...</div>
      )}
    </div>
  );
}

export default AdminProfile;
