                />
              </Form.Group>

              <Form.Group>
                <Form.Label>üìç ÿßŸÑÿπŸÜŸàÿßŸÜ</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedStudent.address}
                  onChange={(e) => setSelectedStudent({ ...selectedStudent, address: e.target.value })}
                />
              </Form.Group>

              <Form.Group>
                <Form.Label>ÿßŸÑÿ≠ÿßŸÑÿ©</Form.Label>
                <Form.Select
                  value={selectedStudent.status}
                  onChange={(e) => setSelectedStudent({ ...selectedStudent, status: e.target.value })}
                >
                  <option value="ŸÜÿ¥ÿ∑">ŸÜÿ¥ÿ∑</option>
                  <option value="ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑">ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑</option>
                </Form.Select>
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="danger" onClick={handleDelete}>‚ùå ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®</Button>
            <Button variant="secondary" onClick={() => setShowModal(false)}>üîô ÿ±ÿ¨Ÿàÿπ</Button>
            <Button variant="success" onClick={handleSaveChanges}>‚úÖ ÿ™ÿ£ŸÉŸäÿØ</Button>
          </Modal.Footer>
        </Modal>
      )}
    </Container>
  );
}

export default ManageStudents;
