import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner, Table } from 'react-bootstrap';
import axios from 'axios';

function AddStudentCourses() {
  const [courses, setCourses] = useState([]);
  const [students, setStudents] = useState([]);
  const [studentCourses, setStudentCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedStudentId, setSelectedStudentId] = useState('');
  const [selectedCourseId, setSelectedCourseId] = useState('');
  const [selectedId, setSelectedId] = useState(null);

  useEffect(() => {
    fetchCourses();
    fetchStudents();
    fetchStudentCourses();
  }, []);

  const fetchCourses = () => {
    axios.get('http://localhost:5000/api/admin/courses')
      .then(response => {
        setCourses(response.data);
      })
      .catch(error => {
        console.error('Error fetching courses:', error);
      });
  };

  const fetchStudents = () => {
    axios.get('http://localhost:5000/api/admin/students')
      .then(response => {
        setStudents(response.data);
      })
      .catch(error => {
        console.error('Error fetching students:', error);
      });
  };

  const fetchStudentCourses = () => {
    setLoading(true);
    axios.get('http://localhost:5000/api/admin/student-course')
      .then(response => {
        const studentCoursesWithNames = response.data.map(item => {
          const student = students.find(student => student.id === item.student_id);
          const course = courses.find(course => course.id === item.course_id);
          return {
            ...item,
            student_name: student ? student.full_name : 'غير معروف',
            course_name: course ? course.name : 'غير معروف',
          };
        });
        setStudentCourses(studentCoursesWithNames);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching student courses:', error);
        setLoading(false);
      });
  };

  const handleDelete = (studentId) => {
    axios.delete(`http://localhost:5000/api/admin/student-course/${studentId}`)
      .then(response => {
        alert('تم حذف التسجيلات بنجاح!');
        fetchStudentCourses();  // إعادة تحميل البيانات بعد الحذف
      })
      .catch(error => {
        console.error('Error deleting student courses:', error);
        alert('حدث خطأ أثناء الحذف.');
      });
  };

  const handleAddOrUpdate = () => {
    if (!selectedStudentId || !selectedCourseId) {
      alert('يرجى اختيار الطالب والمادة');
      return;
    }

    const data = {
      student_id: selectedStudentId,
      course_id: selectedCourseId,
    };

    if (selectedId) {
      axios.put(`/api/student-courses/${selectedId}`, data)
        .then(response => {
          alert('تم تعديل التسجيل بنجاح!');
          fetchStudentCourses();
          resetForm();
        })
        .catch(error => {
          console.error('Error updating student course:', error);
        });
    } else {
      axios.post('http://localhost:5000/api/admin/student-course', data)
        .then(response => {
          alert('تم إضافة الطالب إلى المادة بنجاح!');
          fetchStudentCourses();
          resetForm();
        })
        .catch(error => {
          console.error('Error adding student course:', error);
        });
    }
  };

  const resetForm = () => {
    setSelectedStudentId('');
    setSelectedCourseId('');
    setSelectedId(null);
  };

  const handleEdit = (item) => {
    setSelectedId(item.id);
    setSelectedStudentId(item.student_id);
    setSelectedCourseId(item.course_id);
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white p-3">
              <h5 className="fw-bold m-0">{selectedId ? 'تعديل تسجيل' : 'إضافة طالب إلى مادة'}</h5>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  <Form>
                    <Form.Group>
                      <Form.Label>اختر الطالب</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedStudentId}
                        onChange={(e) => setSelectedStudentId(e.target.value)}
                        disabled={selectedId}
                      >
                        <option value="">اختر الطالب</option>
                        {students.map((student) => (
                          <option key={student.id} value={student.id}>
                            {student.full_name}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>اختر المادة</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedCourseId}
                        onChange={(e) => setSelectedCourseId(e.target.value)}
                        disabled={selectedId}
                      >
                        <option value="">اختر المادة</option>
                        {courses.map((course) => (
                          <option key={course.id} value={course.id}>
                            {course.name}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Button variant="success" onClick={handleAddOrUpdate} className="mt-3">
                      {selectedId ? 'تعديل تسجيل' : 'إضافة'}
                    </Button>
                  </Form>

                  <h6 className="mt-4">جميع التسجيلات</h6>
                  <Table striped bordered hover className="mt-3">
                    <thead>
                      <tr>
                        <th>اسم الطالب</th>
                        <th>اسم المادة</th>
                        <th>تعديل</th>
                        <th>حذف</th>
                      </tr>
                    </thead>
                    <tbody>
                      {studentCourses.length > 0 ? (
                        studentCourses.map((item) => (
                          <tr key={item.id}>
                            <td>{item.student_name}</td>
                            <td>{item.course_name}</td>
                            <td>
                              <Button variant="warning" onClick={() => handleEdit(item)}>
                                تعديل
                              </Button>
                            </td>
                            <td>
                              <Button variant="danger" onClick={() => handleDelete(item.student_id)}>
                                حذف
                              </Button>
                            </td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="4" className="text-center">لا توجد بيانات</td>
                        </tr>
                      )}
                    </tbody>
                  </Table>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AddStudentCourses;
