import React, { useState, useEffect } from "react";
import axios from "axios";

function ManageGrades() {
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingGradeId, setEditingGradeId] = useState(null);
  const [editedGrades, setEditedGrades] = useState({});
  const [newGrade, setNewGrade] = useState({ student_id: "", course_id: "", midterm: "", final: "" });

  useEffect(() => {
    fetchGrades();
  }, []);

  const fetchGrades = () => {
    setLoading(true);
    axios.get("http://localhost:5000/api/admin/grades")
      .then((response) => {
        setGrades(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching grades", error);
        setLoading(false);
      });
  };

  const handleEditClick = (grade) => {
    setEditingGradeId(grade.id);
    setEditedGrades({ midterm: grade.midterm, final: grade.final });
  };

  const handleChange = (e, field) => {
    setEditedGrades({ ...editedGrades, [field]: e.target.value });
  };

  const handleSave = (id) => {
    const updatedGrade = {
      midterm: editedGrades.midterm,
      final: editedGrades.final,
      total: parseInt(editedGrades.midterm) + parseInt(editedGrades.final),
      status: parseInt(editedGrades.midterm) + parseInt(editedGrades.final) >= 50 ? "ناجح" : "راسب",
    };

    axios.put(`http://localhost:5000/api/admin/grade/${id}`, updatedGrade)
      .then(() => {
        fetchGrades();
        setEditingGradeId(null);
      })
      .catch((error) => console.error("Error updating grade", error));
  };

  const handleNewGradeChange = (e) => {
    setNewGrade({ ...newGrade, [e.target.name]: e.target.value });
  };

  const handleAddGrade = () => {
    const gradeData = {
      student_id: newGrade.student_id,
      course_id: newGrade.course_id,
      midterm: parseInt(newGrade.midterm),
      final: parseInt(newGrade.final),
      total: parseInt(newGrade.midterm) + parseInt(newGrade.final),
      status: parseInt(newGrade.midterm) + parseInt(newGrade.final) >= 50 ? "ناجح" : "راسب",
    };

    axios.post("http://localhost:5000/api/admin/grade", gradeData)
      .then(() => {
        fetchGrades();
        setNewGrade({ student_id: "", course_id: "", midterm: "", final: "" });
      })
      .catch((error) => console.error("Error adding grade", error));
  };

  return (
    <div className="container mx-auto p-6">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-xl font-bold mb-4 text-center">إدارة العلامات</h2>
        <div className="mb-6 grid grid-cols-5 gap-4">
          <input type="text" name="student_id" value={newGrade.student_id} onChange={handleNewGradeChange} className="border p-2 rounded" placeholder="رقم الطالب" />
          <input type="text" name="course_id" value={newGrade.course_id} onChange={handleNewGradeChange} className="border p-2 rounded" placeholder="رقم المادة" />
          <input type="number" name="midterm" value={newGrade.midterm} onChange={handleNewGradeChange} className="border p-2 rounded" placeholder="النصفي" />
          <input type="number" name="final" value={newGrade.final} onChange={handleNewGradeChange} className="border p-2 rounded" placeholder="النهائي" />
          <button onClick={handleAddGrade} className="bg-green-500 text-white px-4 py-2 rounded">إضافة</button>
        </div>

        {loading ? (
          <p className="text-center">جاري التحميل...</p>
        ) : (
          <table className="w-full border-collapse border border-gray-300 text-center">
            <thead>
              <tr className="bg-gray-100">
                <th className="border p-2">اسم الطالب</th>
                <th className="border p-2">اسم المادة</th>
                <th className="border p-2">درجة النصفي</th>
                <th className="border p-2">درجة النهائي</th>
                <th className="border p-2">الإجمالي</th>
                <th className="border p-2">الحالة</th>
                <th className="border p-2">الإجراءات</th>
              </tr>
            </thead>
            <tbody>
              {grades.map((grade) => (
                <tr key={grade.id} className="border">
                  <td className="border p-2">{grade.student_name}</td>
                  <td className="border p-2">{grade.course_name}</td>
                  <td className="border p-2">
                    {editingGradeId === grade.id ? (
                      <input type="number" value={editedGrades.midterm} onChange={(e) => handleChange(e, "midterm")} className="border p-1" />
                    ) : (
                      grade.midterm
                    )}
                  </td>
                  <td className="border p-2">
                    {editingGradeId === grade.id ? (
                      <input type="number" value={editedGrades.final} onChange={(e) => handleChange(e, "final")} className="border p-1" />
                    ) : (
                      grade.final
                    )}
                  </td>
                  <td className="border p-2">{grade.total}</td>
                  <td className="border p-2">{grade.status === "Pass" ? "ناجح" : "راسب"}</td>
                  <td className="border p-2">
                    {editingGradeId === grade.id ? (
                      <button onClick={() => handleSave(grade.id)} className="bg-green-500 text-white px-3 py-1 rounded">حفظ</button>
                    ) : (
                      <button onClick={() => handleEditClick(grade)} className="bg-blue-500 text-white px-3 py-1 rounded">تعديل</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ManageGrades;
