import React from "react";
import { Card, ListGroup, Button, Container, Row, Col } from "react-bootstrap";

function AdminProfile() {
  // بيانات افتراضية
  const adminData = {
    name: "John Doe",
    email: "admin@example.com",
    role: "Administrator",
    phone: "+123 456 7890",
    address: "123 Admin Street, City, Country",
  };

  return (
    <Container className="mt-4">
      <Row>
        {/* تفاصيل الحساب على اليسار */}
        <Col md={8}>
          <Card className="shadow-sm">
            <Card.Header className="bg-primary text-white">تفاصيل الحساب</Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item><strong>البريد الإلكتروني:</strong> {adminData.email}</ListGroup.Item>
              <ListGroup.Item><strong>رقم الهاتف:</strong> {adminData.phone}</ListGroup.Item>
              <ListGroup.Item><strong>العنوان:</strong> {adminData.address}</ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>

        {/* اسم الأدمن والدور على اليمين */}
        <Col md={4} className="text-end d-flex flex-column justify-content-center">
          <h4>{adminData.name}</h4>
          <p className="text-muted">{adminData.role}</p>
          <Button variant="primary" size="sm" className="align-self-end">
            تعديل الملف الشخصي
          </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
