import React, { useState, useEffect } from "react";
import { Card, Button, Container, Row, Col, Form, Spinner, Modal } from "react-bootstrap";
import axios from "axios";

function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newStudent, setNewStudent] = useState({
    full_name: "",
    email: "",
    phone: "",
    address: "",
    status: "نشط",
    password: "",
  });
  const [showConfirmation, setShowConfirmation] = useState(false);

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    axios
      .get("http://localhost:5000/api/admin/students", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setStudents(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching students", error);
        setLoading(false);
      });
  };

  const handleEditClick = (student) => {
    setSelectedStudent(student);
    setShowModal(true);
  };

  const filteredStudents = students.filter((student) =>
    student.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.phone.includes(searchTerm) ||
    student.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container className="mt-4 text-center bg-light p-4 rounded shadow-lg">
      <h2 className="text-primary mb-4">Manage Students</h2>
      <Row className="justify-content-center">
        <Col md={8}>
          <Form.Control
            type="text"
            placeholder="Search for a student..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="mb-3 shadow-sm rounded"
          />
          <Button variant="success" className="mb-4 shadow-sm">
            + Add Student
          </Button>
          {loading ? (
            <Spinner animation="border" variant="primary" />
          ) : (
            <div className="student-list">
              {filteredStudents.length > 0 ? (
                filteredStudents.map((student) => (
                  <Card key={student.id} className="mb-3 shadow-sm">
                    <Card.Body>
                      <Card.Title className="text-secondary">{student.full_name}</Card.Title>
                      <Card.Text>Email: {student.email}</Card.Text>
                      <Card.Text>Phone: {student.phone}</Card.Text>
                      <Card.Text>Address: {student.address}</Card.Text>
                      <Card.Text>Status: {student.status}</Card.Text>
                      <Button variant="primary">Edit</Button>
                    </Card.Body>
                  </Card>
                ))
              ) : (
                <p>No students available.</p>
              )}
            </div>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default ManageStudents;
