import React, { useState } from 'react';
import { Button, Form, Card, Container, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

// استيراد ملف CSS الخاص بـ Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    const loginData = { email, password };

    axios.post('http://localhost:5000/api/auth/login', loginData)
      .then((response) => {
        const token = response.data.token;
        const userRole = response.data.role;  // تحديد إذا كان طالبًا أو أدمن
        // تخزين التوكن في localStorage
        localStorage.setItem('jwt_token', token);

        // التوجيه بناءً على الدور المسترجع من الخادم
        if (userRole === 'student') {
          navigate('/student-dashboard'); // توجه إلى لوحة تحكم الطالب
        } else if (userRole === 'admin') {
          navigate('/admin-dashboard'); // توجه إلى لوحة تحكم الأدمن
        } else {
          setError('دور المستخدم غير معروف');
        }
      })
      .catch((error) => {
        setError('فشل في تسجيل الدخول، تأكد من صحة البيانات.');
        console.log(error);
      });
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh' }}>
      <Row className="justify-content-center w-100">
        <Col md={6} sm={12}>
          <Card className="shadow-lg">
            <Card.Body>
              <Card.Title className="text-center mb-4">تسجيل الدخول</Card.Title>
              {error && <div className="alert alert-danger">{error}</div>}
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formEmail" className="mb-3">
                  <Form.Label>البريد الإلكتروني</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="أدخل بريدك الإلكتروني"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </Form.Group>

                <Form.Group controlId="formPassword" className="mb-3">
                  <Form.Label>كلمة المرور</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="أدخل كلمة المرور"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </Form.Group>

                <Button variant="primary" type="submit" block>
                  تسجيل الدخول
                </Button>
              </Form>
            </Card.Body>
            <Card.Footer className="text-center">
              <small>إذا كنت لا تملك حسابًا؟ <a href="/register">تسجيل حساب جديد</a></small>
            </Card.Footer>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default LoginPage;
