import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ViewCourses() {
  const [courses, setCourses] = useState([]);
  
  useEffect(() => {
    const studentId = localStorage.getItem('studentId');  // استرجاع الـ id من localStorage
    if (studentId) {
      axios.get(`http://localhost:5000/api/student/courses-student/${studentId}`)
        .then(response => {
          setCourses(response.data); // حفظ الدورات في الحالة
        })
        .catch(error => {
          console.error('Error fetching courses:', error);
        });
    }
  }, []);

  return (
    <div>
      <h1>View Courses</h1>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            {course.name} - {course.department} - {course.credits} credits
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ViewCourses;
