import React, { useState, useEffect } from "react";
import {
  Card,
  Button,
  Container,
  Row,
  Col,
  Form,
  Spinner,
  Modal,
} from "react-bootstrap";
import axios from "axios";

function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showConfirmation, setShowConfirmation] = useState(false); // ÿ≠ÿßŸÑÿ© ŸÑÿ•ÿ∏Ÿáÿßÿ± ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿ™ÿ£ŸÉŸäÿØ
  const [newStudent, setNewStudent] = useState({
    full_name: "",
    email: "",
    phone: "",
    address: "",
    status: "ŸÜÿ¥ÿ∑",
  }); // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ∑ÿßŸÑÿ® ÿßŸÑÿ¨ÿØŸäÿØ
  const [showAddModal, setShowAddModal] = useState(false); // ÿ≠ÿßŸÑÿ© ŸÑŸÅÿ™ÿ≠ ŸÜÿßŸÅÿ∞ÿ© ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ∑ÿßŸÑÿ®

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    axios
      .get("http://localhost:5000/api/admin/students", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then((response) => {
        setStudents(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching students", error);
        setLoading(false);
      });
  };

  const handleEditClick = (student) => {
    setSelectedStudent(student);
    setShowModal(true);
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:5000/api/admin/student/${selectedStudent.id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
        },
      })
      .then(() => {
        setShowConfirmation(true); // ÿ•ÿ∏Ÿáÿßÿ± ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿ™ÿ£ŸÉŸäÿØ
        setTimeout(() => {
          setShowModal(false);
          fetchStudents();
          setShowConfirmation(false); // ÿ•ÿÆŸÅÿßÿ° ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ÿπÿØ ŸÖÿØÿ© ŸÇÿµŸäÿ±ÿ©
        }, 2000); // ÿ•ÿÆŸÅÿßÿ° ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ÿπÿØ 2 ÿ´ÿßŸÜŸäÿ©
      })
      .catch((error) => console.error("Error deleting student", error));
  };

  const handleSaveChanges = () => {
    axios
      .put(`http://localhost:5000/api/admin/student/${selectedStudent.id}`, {
        full_name: selectedStudent.full_name,
        email: selectedStudent.email,
        phone: selectedStudent.phone,
        address: selectedStudent.address,
        status: selectedStudent.status === "ŸÜÿ¥ÿ∑" ? "active" : "suspended", // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ©
      })
      .then(() => {
        setShowModal(false);
        fetchStudents();
      })
      .catch((error) => console.error("Error updating student", error));
  };

  // ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ® ÿ¨ÿØŸäÿØ
  const handleAddStudent = () => {
    axios
      .post(
        "http://localhost:5000/api/admin/add-student",
        {
          full_name: newStudent.full_name,
          email: newStudent.email,
          phone: newStudent.phone,
          address: newStudent.address,
          status: newStudent.status === "ŸÜÿ¥ÿ∑" ? "active" : "suspended",
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("jwt_token")}`,
          },
        }
      )
      .then(() => {
        setShowAddModal(false);
        fetchStudents();
      })
      .catch((error) => console.error("Error adding student", error));
  };

  const filteredStudents = students.filter(
    (student) =>
      student.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.phone.includes(searchTerm) ||
      student.address.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ∑ŸÑÿßÿ®</h5>
              <div className="d-flex">
                <Form.Control
                  type="text"
                  placeholder="üîç ÿ®ÿ≠ÿ´ ÿπŸÜ ÿ∑ÿßŸÑÿ®..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-50"
                />
                <Button
                  variant="success"
                  onClick={() => setShowAddModal(true)} // ÿ•ÿ∏Ÿáÿßÿ± ŸÜÿßŸÅÿ∞ÿ© ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ®
                  className="ms-3"
                >
                  ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ®
                </Button>
              </div>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : filteredStudents.length > 0 ? (
                filteredStudents.map((student) => (
                  <Card
                    key={student.id}
                    className="mb-3 p-3 shadow-sm border-0"
                  >
                    <Row className="align-items-center">
                      <Col md={2}>
                        <h6 className="fw-bold">{student.full_name}</h6>
                      </Col>
                      <Col md={3}>
                        <p className="mb-1 text-muted">üìß {student.email}</p>
                      </Col>
                      <Col md={2}>
                        <p className="mb-1">üìû {student.phone}</p>
                      </Col>
                      <Col md={3}>
                        <p className="mb-1">üìç {student.address}</p>
                      </Col>
                      <Col md={1}>
                        <p
                          className={`badge bg-${
                            student.status === "ŸÜÿ¥ÿ∑" ? "success" : "danger"
                          }`}
                        >
                          {student.status}
                        </p>
                      </Col>
                      <Col md={1} className="text-end">
                        <Button
                          variant="outline-primary"
                          size="sm"
                          onClick={() => handleEditClick(student)}
                        >
                          ÿ™ÿπÿØŸäŸÑ
                        </Button>
                      </Col>
                    </Row>
                  </Card>
                ))
              ) : (
                <p className="text-center text-muted">ŸÑÿß ŸäŸàÿ¨ÿØ ÿ∑ŸÑÿßÿ® ŸÖÿ™ÿßÿ≠ŸäŸÜ</p>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* ŸÜŸÖŸàÿ∞ÿ¨ ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ® */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ® ÿ¨ÿØŸäÿØ</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.full_name}
                onChange={(e) =>
                  setNewStudent({
                    ...newStudent,
                    full_name: e.target.value,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</Form.Label>
              <Form.Control
                type="email"
                value={newStudent.email}
                onChange={(e) =>
                  setNewStudent({
                    ...newStudent,
                    email: e.target.value,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.phone}
                onChange={(e) =>
                  setNewStudent({
                    ...newStudent,
                    phone: e.target.value,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>üìç ÿßŸÑÿπŸÜŸàÿßŸÜ</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.address}
                onChange={(e) =>
                  setNewStudent({
                    ...newStudent,
                    address: e.target.value,
                  })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Label>ÿßŸÑÿ≠ÿßŸÑÿ©</Form.Label>
              <Form.Select
                value={newStudent.status}
                onChange={(e) =>
                  setNewStudent({
                    ...newStudent,
                    status: e.target.value,
                  })
                }
              >
                <option value="ŸÜÿ¥ÿ∑">ŸÜÿ¥ÿ∑</option>
                <option value="ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑">ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑</option>
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            üîô ÿ±ÿ¨Ÿàÿπ
          </Button>
          <Button variant="success" onClick={handleAddStudent}>
            ‚úÖ ÿ•ÿ∂ÿßŸÅÿ©
          </Button>
        </Modal.Footer>
      </Modal>

      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ≠ÿ∞ŸÅ */}
      {showConfirmation && (
        <div className="alert alert-success mt-3 text-center">
          ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠!
        </div>
      )}
    </Container>
  );
}

export default ManageStudents;
