import React, { useState, useEffect } from 'react';
import { Card, Container, Row, Col, Table, Spinner } from 'react-bootstrap';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // استخدام useParams لجلب المعامل من الـ URL

function ViewGrades() {
  const { student_id } = useParams(); // الحصول على student_id من الـ URL
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGrades();
  }, [student_id]); // جلب الدرجات كلما تغير student_id

  const fetchGrades = () => {
    setLoading(true);
    axios.get(`http://localhost:5000/api/student/grades/${student_id}`, {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => {
      setGrades(response.data);
      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching grades', error);
      setLoading(false);
    });
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">جميع الدرجات</h5>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  <Table striped bordered hover className="mt-3">
                    <thead>
                      <tr>
                        <th>اسم المادة</th>
                        <th>درجة الامتحان النصفي</th>
                        <th>درجة الامتحان النهائي</th>
                        <th>الإجمالي</th>
                        <th>الحالة</th>
                      </tr>
                    </thead>
                    <tbody>
                      {grades.length > 0 ? (
                        grades.map((grade) => (
                          <tr key={grade.id}>
                            <td>{grade.course_name}</td>
                            <td>{grade.midterm}</td>
                            <td>{grade.final}</td>
                            <td>{grade.total}</td>
                            <td>{grade.status}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="5" className="text-center">لا توجد درجات للطالب</td>
                        </tr>
                      )}
                    </tbody>
                  </Table>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ViewGrades;
