import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner, Table } from 'react-bootstrap';
import axios from 'axios';

function ManageGrades() {
  const [courses, setCourses] = useState([]);
  const [students, setStudents] = useState([]);
  const [grades, setGrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCourseId, setSelectedCourseId] = useState('');
  const [selectedStudentId, setSelectedStudentId] = useState('');
  const [midterm, setMidterm] = useState('');
  const [final, setFinal] = useState('');
  const [editGradeId, setEditGradeId] = useState(null);

  useEffect(() => {
    fetchCourses();
    fetchStudents();
    fetchGrades();
  }, []);

  const fetchCourses = () => {
    axios.get('http://localhost:5000/api/admin/courses', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => setCourses(response.data))
    .catch((error) => console.error('Error fetching courses', error));
  };

  const fetchStudents = () => {
    axios.get('http://localhost:5000/api/admin/students', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => setStudents(response.data))
    .catch((error) => console.error('Error fetching students', error));
  };

  const fetchGrades = () => {
    setLoading(true);
    axios.get('http://localhost:5000/api/admin/grades', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then((response) => {
      setGrades(response.data);
      setLoading(false);
    })
    .catch((error) => {
      console.error('Error fetching grades', error);
      setLoading(false);
    });
  };

  const handleAddOrUpdateGrade = () => {
    if (!selectedStudentId || !selectedCourseId || !midterm || !final) {
      alert('يرجى ملء جميع الحقول');
      return;
    }

    const newGrade = {
      student_id: selectedStudentId,
      course_id: selectedCourseId,
      midterm,
      final,
      total: parseInt(midterm) + parseInt(final),
      status: parseInt(midterm) + parseInt(final) >= 50 ? 'ناجح' : 'راسب',
    };

    if (editGradeId) {
      axios.put(`http://localhost:5000/api/admin/grade/${editGradeId}`, newGrade, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchGrades();
        resetForm();
      })
      .catch((error) => console.error('Error updating grade', error));
    } else {
      axios.post('http://localhost:5000/api/admin/grade', newGrade, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchGrades();
        resetForm();
      })
      .catch((error) => console.error('Error adding grade', error));
    }
  };

  const handleEditGrade = (grade) => {
    setEditGradeId(grade.id);
    setSelectedStudentId(grade.student_id);
    setSelectedCourseId(grade.course_id);
    setMidterm(grade.midterm);
    setFinal(grade.final);
  };

  const resetForm = () => {
    setMidterm('');
    setFinal('');
    setSelectedStudentId('');
    setSelectedCourseId('');
    setEditGradeId(null);
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">إدارة العلامات</h5>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  <h6 className="mb-3">{editGradeId ? 'تعديل درجة' : 'إضافة درجة جديدة'}</h6>
                  <Form>
                    <Form.Group>
                      <Form.Label>اختر الطالب</Form.Label>
                      <Form.Control as="select" value={selectedStudentId} onChange={(e) => setSelectedStudentId(e.target.value)}>
                        <option value="">اختر الطالب</option>
                        {students.map((student) => (
                          <option key={student.id} value={student.id}>{student.full_name}</option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>اختر المادة</Form.Label>
                      <Form.Control as="select" value={selectedCourseId} onChange={(e) => setSelectedCourseId(e.target.value)}>
                        <option value="">اختر المادة</option>
                        {courses.map((course) => (
                          <option key={course.id} value={course.id}>{course.name}</option>
                        ))}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>درجة الامتحان النصفي</Form.Label>
                      <Form.Control type="number" value={midterm} onChange={(e) => setMidterm(e.target.value)} />
                    </Form.Group>

                    <Form.Group className="mt-3">
                      <Form.Label>درجة الامتحان النهائي</Form.Label>
                      <Form.Control type="number" value={final} onChange={(e) => setFinal(e.target.value)} />
                    </Form.Group>

                    <Button variant="success" onClick={handleAddOrUpdateGrade} className="mt-3">
                      {editGradeId ? 'حفظ التعديلات' : 'إضافة درجة'}
                    </Button>
                  </Form>

                  <h6 className="mt-4">جميع الدرجات</h6>
                  <Table striped bordered hover className="mt-3">
                    <thead>
                      <tr>
                        <th>اسم الطالب</th>
                        <th>اسم المادة</th>
                        <th>درجة الامتحان النصفي</th>
                        <th>درجة الامتحان النهائي</th>
                        <th>الإجمالي</th>
                        <th>الحالة</th>
                        <th>الإجراء</th>
                      </tr>
                    </thead>
                    <tbody>
                      {grades.map((grade) => (
                        <tr key={grade.id}>
                          <td>{grade.student_name}</td>
                          <td>{grade.course_name}</td>
                          <td>{grade.midterm}</td>
                          <td>{grade.final}</td>
                          <td>{grade.total}</td>
                          <td>{grade.status === 'Pass' ? 'ناجح' : 'راسب'}</td>
                          <td>
                            <Button variant="warning" size="sm" onClick={() => handleEditGrade(grade)}>تعديل</Button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageGrades;
