import React, { useEffect, useState } from "react";
import { Card, ListGroup, Button, Container, Row, Col } from "react-bootstrap";
import axios from "axios";

function AdminProfile() {
  const [userInfo, setUserInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    // الحصول على التوكن من localStorage
    const token = localStorage.getItem("jwt_token");

    // إرسال الطلب باستخدام التوكن
    if (token) {
      axios
        .get("http://localhost:5000/api/user", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setUserInfo(response.data); // تعيين بيانات المستخدم
        })
        .catch((err) => {
          setError("فشل في جلب البيانات، تأكد من صحة التوكن.");
        });
    } else {
      setError("لا يوجد توكن صالح.");
    }
  }, []);

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        {/* القسم الرئيسي - تفاصيل الحساب */}
        <Col md={8}>
          <Card className="shadow-sm h-100">
            <Card.Header className="bg-primary text-white">تفاصيل الحساب</Card.Header>
            {error && <div className="alert alert-danger">{error}</div>}
            {userInfo ? (
              <ListGroup variant="flush">
                <ListGroup.Item><strong>الاسم:</strong> {userInfo.full_name}</ListGroup.Item>
                <ListGroup.Item><strong>البريد الإلكتروني:</strong> {userInfo.email}</ListGroup.Item>
                <ListGroup.Item><strong>رقم الهاتف:</strong> {userInfo.phone}</ListGroup.Item>
                <ListGroup.Item><strong>العنوان:</strong> {userInfo.address}</ListGroup.Item>
              </ListGroup>
            ) : (
              <div>جاري تحميل البيانات...</div>
            )}
          </Card>
        </Col>

        {/* القسم الجانبي - معلومات الأدمن */}
        <Col md={4}>
          <Card className="shadow-sm text-center h-100 d-flex flex-column justify-content-center">
            <Card.Body>
              {userInfo ? (
                <>
                  <h4 className="fw-bold text-primary">{userInfo.full_name}</h4>
                  <p className="text-muted">{userInfo.role}</p>
                  <Button variant="outline-primary" size="sm">
                    تعديل الملف الشخصي
                  </Button>
                </>
              ) : (
                <div>جاري تحميل البيانات...</div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
