import React, { useState, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Form, Spinner } from 'react-bootstrap';
import axios from 'axios';

function ManageCourses() {
  const [courses, setCourses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [editingCourseId, setEditingCourseId] = useState(null);
  const [editedCourse, setEditedCourse] = useState({});
  const [addingCourse, setAddingCourse] = useState(false);
  const [newCourse, setNewCourse] = useState({ name: '', department: '', credits: '' });

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = () => {
    axios
      .get('http://localhost:5000/api/admin/courses', {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then((response) => {
        setCourses(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching courses', error);
        setLoading(false);
      });
  };

  const handleEditClick = (course) => {
    setEditingCourseId(course.id);
    setEditedCourse({ ...course });
  };

  const handleSaveEdit = () => {
    axios
      .put(`http://localhost:5000/api/admin/course/${editedCourse.id}`, editedCourse, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchCourses();
        setEditingCourseId(null);
      })
      .catch((error) => console.error('Error updating course', error));
  };

  const handleDeleteCourse = (id) => {
    axios
      .delete(`http://localhost:5000/api/admin/course/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => fetchCourses())
      .catch((error) => console.error('Error deleting course', error));
  };

  const handleAddCourse = () => {
    axios
      .post('http://localhost:5000/api/admin/add-course', newCourse, {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
      })
      .then(() => {
        fetchCourses();
        setAddingCourse(false);
        setNewCourse({ name: '', department: '', credits: '' });
      })
      .catch((error) => console.error('Error adding course', error));
  };

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10}>
          <Card className="shadow-sm border-0">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center p-3">
              <h5 className="fw-bold m-0">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸàÿßÿØ</h5>
              <Form.Control
                type="text"
                placeholder="üîç ÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖÿßÿØÿ©..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-50 mx-2"
              />
              <Button variant="light" onClick={() => setAddingCourse(true)}>‚ûï ÿ•ÿ∂ÿßŸÅÿ© ŸÖÿßÿØÿ©</Button>
            </Card.Header>

            <Card.Body>
              {loading ? (
                <div className="text-center my-4">
                  <Spinner animation="border" variant="primary" />
                </div>
              ) : (
                <>
                  {addingCourse && (
                    <Card className="mb-3 p-3 shadow-sm border-0">
                      <Row className="align-items-center">
                        <Col md={3}>
                          <Form.Control
                            type="text"
                            placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÖÿßÿØÿ©"
                            value={newCourse.name}
                            onChange={(e) => setNewCourse({ ...newCourse, name: e.target.value })}
                          />
                        </Col>
                        <Col md={2}>
                          <Form.Control
                            type="text"
                            placeholder="ÿßŸÑŸÇÿ≥ŸÖ"
                            value={newCourse.department}
                            onChange={(e) => setNewCourse({ ...newCourse, department: e.target.value })}
                          />
                        </Col>
                        <Col md={2}>
                          <Form.Control
                            type="number"
                            placeholder="ÿπÿØÿØ ÿßŸÑÿ≥ÿßÿπÿßÿ™"
                            value={newCourse.credits}
                            onChange={(e) => setNewCourse({ ...newCourse, credits: e.target.value })}
                          />
                        </Col>
                        <Col md={3} className="d-flex gap-2">
                          <Button variant="success" onClick={handleAddCourse}>‚úÖ ÿ•ÿ∂ÿßŸÅÿ©</Button>
                          <Button variant="secondary" onClick={() => setAddingCourse(false)}>üîô ÿ•ŸÑÿ∫ÿßÿ°</Button>
                        </Col>
                      </Row>
                    </Card>
                  )}
                  {courses.filter((course) =>
                    course.name.toLowerCase().includes(searchTerm.toLowerCase())
                  ).map((course) => (
                    <Card key={course.id} className="mb-3 p-3 shadow-sm border-0">
                      <Row className="align-items-center">
                        {editingCourseId === course.id ? (
                          <>
                            <Col md={3}>
                              <Form.Control
                                type="text"
                                value={editedCourse.name}
                                onChange={(e) => setEditedCourse({ ...editedCourse, name: e.target.value })}
                              />
                            </Col>
                            <Col md={2}>
                              <Form.Control
                                type="text"
                                value={editedCourse.department}
                                onChange={(e) => setEditedCourse({ ...editedCourse, department: e.target.value })}
                              />
                            </Col>
                            <Col md={2}>
                              <Form.Control
                                type="number"
                                value={editedCourse.credits}
                                onChange={(e) => setEditedCourse({ ...editedCourse, credits: e.target.value })}
                              />
                            </Col>
                            <Col md={3} className="d-flex gap-2">
                              <Button variant="success" onClick={handleSaveEdit}>‚úÖ ÿ≠ŸÅÿ∏</Button>
                              <Button variant="secondary" onClick={() => setEditingCourseId(null)}>üîô ÿ•ŸÑÿ∫ÿßÿ°</Button>
                              <Button variant="danger" onClick={() => handleDeleteCourse(course.id)}>üóëÔ∏è ÿ≠ÿ∞ŸÅ</Button>
                            </Col>
                          </>
                        ) : (
                          <>
                            <Col md={3}><h6 className="fw-bold">{course.name}</h6></Col>
                            <Col md={2}><p className="mb-1">{course.department}</p></Col>
                            <Col md={2}><p className="mb-1">{course.credits} ÿ≥ÿßÿπÿßÿ™</p></Col>
                            <Col md={3} className="d-flex gap-2">
                              <Button variant="warning" onClick={() => handleEditClick(course)}>‚úèÔ∏è ÿ™ÿπÿØŸäŸÑ</Button>
                            </Col>
                          </>
                        )}
                      </Row>
                    </Card>
                  ))}
                </>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default ManageCourses;
