import React from 'react';
import { Navigate } from 'react-router-dom';
const navigate = useNavigate(); // استيراد useNavigate للعودة إلى الصفحة السابقة

const PrivateRoute = ({ element: Element, requiredRole, ...rest }) => {
  const token = localStorage.getItem('jwt_token'); // تحقق من وجود التوكن في localStorage
  const role = localStorage.getItem('role'); // استرجاع الدور من localStorage (يجب أن تقوم بتخزينه عند تسجيل الدخول)

  if (!token) {
    // إذا لم يوجد التوكن، أعد توجيه المستخدم إلى صفحة تسجيل الدخول
    return <Navigate to="/" />;
  }

  if (requiredRole && requiredRole !== role) {
    // إذا كان الدور غير متوافق مع المطلوب، أعد توجيه المستخدم إلى الصفحة السابقة
    navigate(-1); // العودة إلى الصفحة السابقة
    return null; // يجب أن يكون المكون فارغًا لأننا سنقوم بالتوجيه مباشرة
  }
  // إذا كان التوكن موجودًا والدور صحيح، يعرض المكون المحدد
  return <Element {...rest} />;
};

export default PrivateRoute;
