import React, { useState } from 'react';
import { Button, Nav } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ManageStudents from './ManageStudents';
import AdminProfile    from './AdminProfile';
import ManageCourses   from './ManageCourses';
import ManageGrades    from './ManageGrades';
import AddStudentCourses from './Add-student-courses';
import AddAdmin        from './AddAdmin';
import Cookies         from 'js-cookie';

// ** تأكد إنك فعلت axios.defaults.withCredentials = true **

function AdminDashboard() {
  const [activeComponent, setActiveComponent] = useState('profile');
  const navigate = useNavigate();

  const handleLogout = () => {
    axios
      .post("/api/auth/logout")  // withCredentials مفعّل افتراضيًا
      .then(() => {
        // نحذف الكوكيز العادية اللي ممكن تقرأها من الجافاسكربت
        Cookies.remove('student_id');
        Cookies.remove('role');

        // الـ jwt_token HttpOnly سينحذف من المتصفح أو يستبدل بناءً على Set-Cookie من السيرفر
        navigate('/');
      })
      .catch(err => {
        console.error("فشل تسجيل الخروج", err);
      });
  };

  const renderComponent = () => {
    switch (activeComponent) {
      case 'profile':         return <AdminProfile />;
      case 'students':        return <ManageStudents />;
      case 'courses':         return <ManageCourses />;
      case 'grades':          return <ManageGrades />;
      case 'student-courses': return <AddStudentCourses />;
      case 'add-admin':       return <AddAdmin />;
      default:                return <AdminProfile />;
    }
  };

  return (
    <div className="d-flex" style={{ minHeight: '100vh' }}>
      <div className="bg-sidebar text-white p-3" style={{ width: '250px' }}>
        <h3 className="text-center mb-4">لوحة التحكم</h3>
        <Nav className="flex-column">
          {["profile","students","courses","grades","student-courses","add-admin"].map(item => (
            <Nav.Link
              key={item}
              onClick={() => setActiveComponent(item)}
              className={`text-white sidebar-link ${activeComponent===item?'active':''}`}
            >
              {{
                profile: "معلومات الأدمن",
                students: "إدارة الطلاب",
                courses: "إدارة المواد",
                grades: "إدارة العلامات",
                "student-courses": "إضافة مواد للطالب",
                "add-admin": "إضافة أدمن"
              }[item]}
            </Nav.Link>
          ))}
          <hr className="bg-white" />
          <Button variant="danger" onClick={handleLogout} className="mt-3 w-100">
            تسجيل الخروج
          </Button>
        </Nav>
      </div>
      <div className="flex-grow-1 p-4" style={{ backgroundColor: '#f8f9fa' }}>
        {renderComponent()}
      </div>
      <style jsx="true">{`
        .bg-sidebar { background-color: #2c3e50; }
        .sidebar-link {
          margin: 5px 0; padding: 10px; border-radius: 5px;
          transition: all .2s ease-in-out;
        }
        .sidebar-link:hover,
        .sidebar-link.active {
          background-color: #1abc9c; color: #fff;
        }
      `}</style>
    </div>
  );
}

export default AdminDashboard;
