import React from "react";
import { Card, ListGroup, Button, Container, Row, Col, Image } from "react-bootstrap";

function AdminProfile() {
  // بيانات افتراضية
  const adminData = {
    name: "John Doe",
    email: "admin@example.com",
    role: "Administrator",
    phone: "+123 456 7890",
    address: "123 Admin Street, City, Country",
    profilePic: "https://via.placeholder.com/150", // صورة افتراضية
  };

  return (
    <Container className="mt-4">
      <Row>
        {/* القسم الجانبي - صورة ومعلومات الأدمن */}
        <Col md={4}>
          <Card className="shadow-sm text-center p-3">
           
            <h5>{adminData.name}</h5>
            <p className="text-muted">{adminData.role}</p>
            <Button variant="primary" size="sm">
              تعديل الملف الشخصي
            </Button>
          </Card>
        </Col>

        {/* القسم الرئيسي - تفاصيل الحساب */}
        <Col md={8}>
          <Card className="shadow-sm">
            <Card.Header className="bg-primary text-white">تفاصيل الحساب</Card.Header>
            <ListGroup variant="flush">
              <ListGroup.Item><strong>البريد الإلكتروني:</strong> {adminData.email}</ListGroup.Item>
              <ListGroup.Item><strong>رقم الهاتف:</strong> {adminData.phone}</ListGroup.Item>
              <ListGroup.Item><strong>العنوان:</strong> {adminData.address}</ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default AdminProfile;
