import React, { useState, useEffect } from 'react';
import { Tab, Tabs, Button } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import StudentProfile from './StudentProfile';  // صفحة عرض وتعديل معلومات الطالب
import ViewCourses from './ViewCourses';  // صفحة عرض المواد الدراسية
import ViewGrades from './ViewGrades';  // صفحة عرض العلامات

function StudentDashboard() {
  const [key, setKey] = useState('profile');
  const navigate = useNavigate();
  const { student_id } = useParams(); // الحصول على student_id من URL

  // وظيفة تسجيل الخروج
  const handleLogout = () => {
    // حذف التوكن من localStorage
    localStorage.removeItem('jwt_token');
    // إعادة التوجيه إلى صفحة تسجيل الدخول
    navigate('/');
  };

  // التأكد من أن student_id موجود في الـ URL
  useEffect(() => {
    if (!student_id) {
      // إذا لم يكن هناك student_id في الـ URL، إعادة التوجيه إلى صفحة تسجيل الدخول أو صفحة أخرى
      navigate('/');
    }
  }, [student_id, navigate]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between">
        <h2>لوحة التحكم للطالب</h2>
        <Button variant="danger" onClick={handleLogout}>تسجيل الخروج</Button>
      </div>
      
      <Tabs activeKey={key} onSelect={(k) => setKey(k)} className="mb-3">
        <Tab eventKey="profile" title="معلومات الطالب">
          <StudentProfile />  {/* عرض وتعديل معلومات الطالب */}
        </Tab>
        <Tab eventKey="courses" title="عرض المواد الدراسية">
          <ViewCourses />  {/* عرض المواد التي يتابعها الطالب */}
        </Tab>
        <Tab eventKey="grades" title="عرض العلامات">
          {student_id && <ViewGrades student_id={student_id} />}  {/* التأكد من أن student_id موجود */}
        </Tab>
      </Tabs>
    </div>
  );
}

export default StudentDashboard;
