import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

// الصفحات
import AdminProfile from './pages/AdminProfile';
import ManageStudents from './pages/ManageStudents';
import ManageCourses from './pages/ManageCourses';
import ManageGrades from './pages/ManageGrades';
import ManageAttendance from './pages/ManageAttendance';
import AccountSettings from './pages/AccountSettings';

function App() {
  const [activeTab, setActiveTab] = useState('profile'); // لتحديد التبويب النشط

  return (
    <Router>
      <div className="d-flex">
        {/* الشريط الجانبي */}
        <div className="bg-light p-3" style={{ width: '250px', height: '100vh' }}>
          <h3>Admin Dashboard</h3>
          <ul className="list-unstyled">
            <li><Link to="/" onClick={() => setActiveTab('profile')} className={activeTab === 'profile' ? 'text-primary' : ''}>معلومات الأدمن</Link></li>
            <li><Link to="/students" onClick={() => setActiveTab('students')} className={activeTab === 'students' ? 'text-primary' : ''}>إدارة الطلاب</Link></li>
            <li><Link to="/courses" onClick={() => setActiveTab('courses')} className={activeTab === 'courses' ? 'text-primary' : ''}>إدارة المواد</Link></li>
            <li><Link to="/grades" onClick={() => setActiveTab('grades')} className={activeTab === 'grades' ? 'text-primary' : ''}>إدارة العلامات</Link></li>
            <li><Link to="/attendance" onClick={() => setActiveTab('attendance')} className={activeTab === 'attendance' ? 'text-primary' : ''}>إدارة الحضور</Link></li>
            <li><Link to="/settings" onClick={() => setActiveTab('settings')} className={activeTab === 'settings' ? 'text-primary' : ''}>إعدادات الحساب</Link></li>
          </ul>
        </div>

        {/* المحتوى الرئيسي */}
        <div className="flex-grow-1 p-3">
          <Routes>
            <Route path="/" element={<AdminProfile />} />
            <Route path="/students" element={<ManageStudents />} />
            <Route path="/courses" element={<ManageCourses />} />
            <Route path="/grades" element={<ManageGrades />} />
            <Route path="/attendance" element={<ManageAttendance />} />
            <Route path="/settings" element={<AccountSettings />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
