import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button, Spinner, Table } from 'react-bootstrap';
import axios from 'axios';

function AddStudentCourses() {
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [studentCourses, setStudentCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [adding, setAdding] = useState(false);

  // جلب قائمة الطلاب (افترض endpoint "http://localhost:5000/api/admin/students")
  const fetchStudents = () => {
    axios.get('http://localhost:5000/api/admin/students', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then(response => {
      setStudents(response.data);
    })
    .catch(error => console.error('Error fetching students', error));
  };

  // جلب قائمة الدورات (افترض endpoint "http://localhost:5000/api/admin/courses")
  const fetchCourses = () => {
    axios.get('http://localhost:5000/api/admin/courses', {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then(response => {
      setCourses(response.data);
    })
    .catch(error => console.error('Error fetching courses', error));
  };

  // جلب الدورات المسجلة للطالب المحدد
  // نفترض هنا endpoint GET "http://localhost:5000/api/admin/student-course/:student_id"
  const fetchStudentCourses = (studentId) => {
    if (!studentId) return;
    setLoading(true);
    axios.get(`http://localhost:5000/api/admin/student-course/${studentId}`, {
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then(response => {
      setStudentCourses(response.data);
      setLoading(false);
    })
    .catch(error => {
      console.error('Error fetching student courses', error);
      setLoading(false);
    });
  };

  useEffect(() => {
    fetchStudents();
    fetchCourses();
  }, []);

  useEffect(() => {
    // عند اختيار طالب، نقوم بجلب الدورات الخاصة به
    if (selectedStudent) {
      fetchStudentCourses(selectedStudent);
    } else {
      setStudentCourses([]);
    }
  }, [selectedStudent]);

  // إضافة مادة للطالب باستخدام endpoint POST "http://localhost:5000/api/admin/student-course"
  const handleAddCourse = () => {
    if (!selectedStudent || !selectedCourse) return;
    setAdding(true);
    axios.post(
      'http://localhost:5000/api/admin/student-course',
      { student_id: selectedStudent, course_id: selectedCourse },
      { headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` } }
    )
    .then(response => {
      fetchStudentCourses(selectedStudent);
      setAdding(false);
      setSelectedCourse(''); // إعادة تعيين المادة المحددة بعد الإضافة
    })
    .catch(error => {
      console.error('Error adding course', error);
      setAdding(false);
    });
  };

  // حذف مادة للطالب باستخدام endpoint DELETE "http://localhost:5000/api/admin/student-course"
  // مع إرسال البيانات في body
  const handleDeleteCourse = (courseId) => {
    axios.delete('http://localhost:5000/api/admin/student-course', {
      data: { student_id: selectedStudent, course_id: courseId },
      headers: { Authorization: `Bearer ${localStorage.getItem('jwt_token')}` },
    })
    .then(response => {
      fetchStudentCourses(selectedStudent);
    })
    .catch(error => console.error('Error deleting course', error));
  };

  return (
    <Container className="mt-4">
      <Card className="shadow-sm border-0">
        <Card.Header className="bg-primary text-white p-3">
          <h5 className="fw-bold m-0">إضافة مواد للطلاب</h5>
        </Card.Header>
        <Card.Body>
          {/* اختيار الطالب والمادة */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>اختر الطالب</Form.Label>
                <Form.Select
                  value={selectedStudent}
                  onChange={(e) => setSelectedStudent(e.target.value)}
                >
                  <option value="">-- اختر طالباً --</option>
                  {students.map(student => (
                    <option key={student.id} value={student.id}>
                      {student.full_name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group>
                <Form.Label>اختر المادة</Form.Label>
                <Form.Select
                  value={selectedCourse}
                  onChange={(e) => setSelectedCourse(e.target.value)}
                >
                  <option value="">-- اختر مادة --</option>
                  {courses.map(course => (
                    <option key={course.id} value={course.id}>
                      {course.name} - {course.department} ({course.credits} ساعات)
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col className="d-flex justify-content-end">
              <Button
                variant="success"
                onClick={handleAddCourse}
                disabled={adding || !selectedStudent || !selectedCourse}
              >
                {adding ? <Spinner animation="border" size="sm" /> : '➕ إضافة المادة'}
              </Button>
            </Col>
          </Row>
          <hr />
          <h5 className="mb-3">المواد المسجلة للطالب</h5>
          {loading ? (
            <div className="text-center my-4">
              <Spinner animation="border" variant="primary" />
            </div>
          ) : studentCourses.length > 0 ? (
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>اسم المادة</th>
                  <th>القسم</th>
                  <th>الساعات</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {studentCourses.map(course => (
                  <tr key={course.id}>
                    <td>{course.name}</td>
                    <td>{course.department}</td>
                    <td>{course.credits} ساعات</td>
                    <td>
                      <Button variant="danger" onClick={() => handleDeleteCourse(course.id)}>
                        🗑️ حذف
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p className="text-center text-muted">لا توجد مواد مسجلة لهذا الطالب</p>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
}

export default AddStudentCourses;
