import React, { useState, useEffect } from "react";
import { Card, Button, Container, Row, Col, Form, Spinner, Modal } from "react-bootstrap";
import axios from "axios";
import Cookies from 'js-cookie';

function ManageStudents() {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newStudent, setNewStudent] = useState({
    full_name: "",
    email: "",
    phone: "",
    address: "",
    status: "ŸÜÿ¥ÿ∑",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "error" ÿ£Ÿà "success"

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    axios.get("http://localhost:5000/api/admin/students", {
      headers: {
        Authorization: `Bearer ${Cookies.get("jwt_token")}`,
      },
    })
      .then((response) => {
        setStudents(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching students", error);
        setLoading(false);
      });
  };

  const handleEditClick = (student) => {
    setSelectedStudent(student);
    setShowModal(true);
  };

  const handleDelete = () => {
    if (window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®ÿü")) {
      axios
        .delete(`http://localhost:5000/api/admin/student/${selectedStudent.id}`, {
          headers: {
            Authorization: `Bearer ${Cookies.get("jwt_token")}`,
          },
        })
        .then(() => {
          setMessage("ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠!");
          setMessageType("success");
          setTimeout(() => {
            setShowModal(false);
            fetchStudents();
          }, 2000);
        })
        .catch((error) => {
          const errorMessage =
            error.response?.data?.message || "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®";
          setMessage(errorMessage);
          setMessageType("error");
        });
    }
  };

  const handleSaveChanges = () => {
    axios
      .put(`http://localhost:5000/api/admin/student/${selectedStudent.id}`, {
        full_name: selectedStudent.full_name,
        email: selectedStudent.email,
        phone: selectedStudent.phone,
        address: selectedStudent.address,
        status: selectedStudent.status === "ŸÜÿ¥ÿ∑" ? "active" : "suspended",
      })
      .then(() => {
        setMessage("ÿ™ŸÖ ÿ™ÿπÿØŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠!");
        setMessageType("success");
        setShowModal(false);
        fetchStudents();
      })
      .catch((error) => {
        const errorMessage =
          error.response?.data?.message || "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿπÿØŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™";
        setMessage(errorMessage);
        setMessageType("error");
      });
  };

  const handleAddStudent = () => {
    axios
      .post("http://localhost:5000/api/admin/add-student", {
        full_name: newStudent.full_name,
        email: newStudent.email,
        phone: newStudent.phone,
        address: newStudent.address,
        status: newStudent.status === "ŸÜÿ¥ÿ∑" ? "active" : "suspended",
        password: newStudent.password,
      })
      .then((res) => {
        setMessage(res.data.message || "ÿ™ŸÖÿ™ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠");
        setMessageType("success");
        fetchStudents();
        setNewStudent({
          full_name: "",
          email: "",
          phone: "",
          address: "",
          status: "ŸÜÿ¥ÿ∑",
          password: "",
        });
      })
      .catch((error) => {
        const errorMessage =
          error.response?.data?.message || "ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ∑ÿßŸÑÿ®";
        setMessage(errorMessage);
        setMessageType("error");
      });
  };

  const filteredStudents = students.filter(
    (student) =>
      student.full_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.phone.includes(searchTerm) ||
      student.address.toLowerCase().includes(searchTerm.toLowerCase())
