import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table, Button, Modal, Form } from "react-bootstrap";

const StudentsPage = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [addError, setAddError] = useState("");
  const [editError, setEditError] = useState("");
  const [newStudent, setNewStudent] = useState({
    full_name: "",
    email: "",
    phone: "",
    address: "",
    status: "نشط",
    password: "",
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = () => {
    axios
      .get("http://localhost:5000/api/admin/students")
      .then((response) => setStudents(response.data))
      .catch((error) => console.error("Error fetching students:", error));
  };

  const handleEdit = (student) => {
    setSelectedStudent({ ...student });
    setShowModal(true);
  };

  const handleSaveChanges = () => {
    setEditError("");
    axios
      .put(`http://localhost:5000/api/admin/student/${selectedStudent.id}`, {
        full_name: selectedStudent.full_name,
        email: selectedStudent.email,
        phone: selectedStudent.phone,
        address: selectedStudent.address,
        status: selectedStudent.status === "نشط" ? "active" : "suspended",
      })
      .then(() => {
        setShowModal(false);
        fetchStudents();
      })
      .catch((error) => {
        if (error.response && error.response.data) {
          const data = error.response.data;
          if (data.errors) {
            setEditError(Object.values(data.errors).join("\n"));
          } else {
            setEditError(data.message || "حدث خطأ أثناء تعديل بيانات الطالب.");
          }
        } else {
          setEditError("فشل الاتصال بالخادم.");
        }
      });
  };

  const handleAddStudent = () => {
    setAddError("");
    axios
      .post("http://localhost:5000/api/admin/add-student", {
        full_name: newStudent.full_name,
        email: newStudent.email,
        phone: newStudent.phone,
        address: newStudent.address,
        status: newStudent.status === "نشط" ? "active" : "suspended",
        password: newStudent.password,
      })
      .then(() => {
        setShowAddModal(false);
        fetchStudents();
        setNewStudent({
          full_name: "",
          email: "",
          phone: "",
          address: "",
          status: "نشط",
          password: "",
        });
      })
      .catch((error) => {
        if (error.response && error.response.data) {
          const data = error.response.data;
          if (data.errors) {
            setAddError(Object.values(data.errors).join("\n"));
          } else {
            setAddError(data.message || "حدث خطأ أثناء إضافة الطالب.");
          }
        } else {
          setAddError("فشل الاتصال بالخادم.");
        }
      });
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">إدارة الطلاب</h2>
      <Button variant="primary" className="mb-3" onClick={() => setShowAddModal(true)}>
        إضافة طالب جديد
      </Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>الاسم الكامل</th>
            <th>البريد الإلكتروني</th>
            <th>رقم الهاتف</th>
            <th>العنوان</th>
            <th>الحالة</th>
            <th>إجراءات</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.full_name}</td>
              <td>{student.email}</td>
              <td>{student.phone}</td>
              <td>{student.address}</td>
              <td>{student.status === "active" ? "نشط" : "موقوف"}</td>
              <td>
                <Button variant="info" size="sm" onClick={() => handleEdit(student)}>
                  تعديل
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal تعديل */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>تعديل الطالب</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedStudent && (
            <Form>
              <Form.Group controlId="formFullName">
                <Form.Label>الاسم الكامل</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedStudent.full_name}
                  onChange={(e) =>
                    setSelectedStudent({ ...selectedStudent, full_name: e.target.value })
                  }
                />
              </Form.Group>
              <Form.Group controlId="formEmail">
                <Form.Label>البريد الإلكتروني</Form.Label>
                <Form.Control
                  type="email"
                  value={selectedStudent.email}
                  onChange={(e) =>
                    setSelectedStudent({ ...selectedStudent, email: e.target.value })
                  }
                />
              </Form.Group>
              <Form.Group controlId="formPhone">
                <Form.Label>رقم الهاتف</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedStudent.phone}
                  onChange={(e) =>
                    setSelectedStudent({ ...selectedStudent, phone: e.target.value })
                  }
                />
              </Form.Group>
              <Form.Group controlId="formAddress">
                <Form.Label>العنوان</Form.Label>
                <Form.Control
                  type="text"
                  value={selectedStudent.address}
                  onChange={(e) =>
                    setSelectedStudent({ ...selectedStudent, address: e.target.value })
                  }
                />
              </Form.Group>
              <Form.Group controlId="formStatus">
                <Form.Label>الحالة</Form.Label>
                <Form.Control
                  as="select"
                  value={selectedStudent.status}
                  onChange={(e) =>
                    setSelectedStudent({ ...selectedStudent, status: e.target.value })
                  }
                >
                  <option value="نشط">نشط</option>
                  <option value="موقوف">موقوف</option>
                </Form.Control>
              </Form.Group>
              {editError && <div className="text-danger mt-2">{editError}</div>}
            </Form>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            إلغاء
          </Button>
          <Button variant="primary" onClick={handleSaveChanges}>
            حفظ التغييرات
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal إضافة */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>إضافة طالب جديد</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="addFullName">
              <Form.Label>الاسم الكامل</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.full_name}
                onChange={(e) => setNewStudent({ ...newStudent, full_name: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="addEmail">
              <Form.Label>البريد الإلكتروني</Form.Label>
              <Form.Control
                type="email"
                value={newStudent.email}
                onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="addPhone">
              <Form.Label>رقم الهاتف</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.phone}
                onChange={(e) => setNewStudent({ ...newStudent, phone: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="addAddress">
              <Form.Label>العنوان</Form.Label>
              <Form.Control
                type="text"
                value={newStudent.address}
                onChange={(e) => setNewStudent({ ...newStudent, address: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="addPassword">
              <Form.Label>كلمة المرور</Form.Label>
              <Form.Control
                type="password"
                value={newStudent.password}
                onChange={(e) => setNewStudent({ ...newStudent, password: e.target.value })}
              />
            </Form.Group>
            <Form.Group controlId="addStatus">
              <Form.Label>الحالة</Form.Label>
              <Form.Control
                as="select"
                value={newStudent.status}
                onChange={(e) => setNewStudent({ ...newStudent, status: e.target.value })}
              >
                <option value="نشط">نشط</option>
                <option value="موقوف">موقوف</option>
              </Form.Control>
            </Form.Group>
            {addError && <div className="text-danger mt-2">{addError}</div>}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            إلغاء
          </Button>
          <Button variant="primary" onClick={handleAddStudent}>
            إضافة
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default StudentsPage;
