import React, { useState, useEffect } from 'react';
import { Container, Card, Row, Col, Form, Button, Spinner, Table } from 'react-bootstrap';
import axios from 'axios';

const AddStudentCourses = () => {
  const [students, setStudents] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [studentCourses, setStudentCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [adding, setAdding] = useState(false);

  useEffect(() => {
    // جلب الطلاب والدورات من الـ API باستخدام axios
    const fetchData = async () => {
      setLoading(true);
      try {
        const studentsResponse = await axios.get('http://localhost:5000/api/students');
        const coursesResponse = await axios.get('http://localhost:5000/api/courses');
        
        setStudents(studentsResponse.data); // تأكد أن الـ API يرجع البيانات بشكل صحيح
        setCourses(coursesResponse.data); // تأكد من نفس الشيء بالنسبة للدورات
      } catch (error) {
        console.error('Error fetching data:', error);
      }
      setLoading(false);
    };

    fetchData();
  }, []);

  const handleAddCourse = async () => {
    if (!selectedStudent || !selectedCourse) return;

    setAdding(true);
    try {
      const response = await axios.post('http://localhost:5000/api/admin/student-course', {
        student_id: selectedStudent,
        course_id: selectedCourse,
      });

      if (response.status === 200) {
        // تحديث قائمة المواد المسجلة للطالب
        const updatedCourses = [...studentCourses, { name: selectedCourse }];
        setStudentCourses(updatedCourses);
      } else {
        console.error('Failed to add course');
      }
    } catch (error) {
      console.error('Error adding course:', error);
    } finally {
      setAdding(false);
    }
  };

  const handleDeleteCourse = async (courseId) => {
    // هنا يمكنك إضافة منطق لحذف مادة من المواد المسجلة
  };

  return (
    <Container className="mt-4">
      <Card className="shadow-sm border-0">
        <Card.Header className="bg-primary text-white p-3">
          <h5 className="fw-bold m-0">إضافة مواد للطلاب</h5>
        </Card.Header>
        <Card.Body>
          {/* اختيار الطالب والمادة */}
          <Row className="mb-3">
            <Col md={6}>
              <Form.Group>
                <Form.Label>اختر الطالب</Form.Label>
                <Form.Select
                  value={selectedStudent}
                  onChange={(e) => setSelectedStudent(e.target.value)}
                >
                  <option value="">-- اختر طالباً --</option>
                  {students.map(student => (
                    <option key={student.id} value={student.id}>
                      {student.full_name}
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group>
                <Form.Label>اختر المادة</Form.Label>
                <Form.Select
                  value={selectedCourse}
                  onChange={(e) => setSelectedCourse(e.target.value)}
                >
                  <option value="">-- اختر مادة --</option>
                  {courses.map(course => (
                    <option key={course.id} value={course.id}>
                      {course.name} - {course.department} ({course.credits} ساعات)
                    </option>
                  ))}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col className="d-flex justify-content-end">
              <Button
                variant="success"
                onClick={handleAddCourse}
                disabled={adding || !selectedStudent || !selectedCourse}
              >
                {adding ? <Spinner animation="border" size="sm" /> : '➕ إضافة المادة'}
              </Button>
            </Col>
          </Row>
          <hr />
          <h5 className="mb-3">المواد المسجلة للطالب</h5>
          {loading ? (
            <div className="text-center my-4">
              <Spinner animation="border" variant="primary" />
            </div>
          ) : studentCourses.length > 0 ? (
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>اسم المادة</th>
                  <th>القسم</th>
                  <th>الساعات</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {studentCourses.map(course => (
                  <tr key={course.id}>
                    <td>{course.name}</td>
                    <td>{course.department}</td>
                    <td>{course.credits} ساعات</td>
                    <td>
                      <Button variant="danger" onClick={() => handleDeleteCourse(course.id)}>
                        🗑️ حذف
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p className="text-center text-muted">لا توجد مواد مسجلة لهذا الطالب</p>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default AddStudentCourses;
